css:
@-moz-document domain("duckduckgo.com") {
.svg.dark-bg .logo_homepage {
  background-image: url('https://duckduckgo.com/assets/logo_homepage.normal.v106.svg') !important;
}

.content-wrap--home {
  height: 340px !important;
}
/* Adjust Homepage Logo */

.logo_homepage {
  height: 171px !important;
  opacity: .8 !important;
}

.logo-wrap--home {
  margin-bottom: 0em !important;
}

/*
  #content_homepage {
  position: relative;
  top: 10%;
    
  opacity: 0.0 !important;
  transition: opacity 2s !important;
}
#content_homepage:hover {
  opacity: 1.0 !important;
  transition: opacity 1s !important; 
}*/

.onboarding-ed { display:none;}

.result {
  color: #FFFFFF!important;
/*background:#000000 !important;
border:1px #D94A26 solid!important;*/
  background-color: rgba(0,0,0,0.1)!important;
  border: 1px #621 solid!important;
}

div.result__snippet > b {
  color: #F64 !important;
}

.site-wrapper,.header-wrap,.page-index,#zci-about {
  background: #222!important;
}

.nav-menu__list,.zci__metabar-wrap,#zci-videos {
  background-color: #222!important;
}

.search--home,.search--adv {
  background: #191919!important;
  border: 1px #D94A26 solid!important;
}

div.acp.acp--selected {
  color: #FFFFFF!important;
  background: #D94A26!important;
  border: 1px #4C4C4C solid!important;
}

.search__autocomplete,.acp {
  color: #EEE !important;
  background: #000  !important;
  border: 1px #222 solid!important;
}

b {
  color: #D94A26!important;
}

a,strong,.result__snippet,#search_form_input_homepage, .content,
.search__input--adv,.info,.zci__content--info,
.nav-menu__heading span,.zci__metabar__primary-text {
  color: #FFFFFF!important;
}


/* Hide: Tagline Homepage, Footer Homepage, Results Logo, Powered By */

#tagline_homepage, #footer_homepage, .X_header__logo, #powered_by, .region-indicator-wrap,
.header-wrap--home, .region-switch {
  visibility: hidden !important;
}

#search_button_homepage {
  color: white;
  /*background-color: #60AE50;*/
  background-color: #d94a26;
}

div.result__snippet {
  color: #999 !important;
}

.region-switch, .tag-home, .site-wrapper-border {
  display: none;
}
}
