@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.tv.com") 
{

/*

 * Author      : CB
 * Date        : September 2008
 * Description : People were complaining about the bright white, so I made it dark brown.
 * Usage       : modify/change/use whatever you want. Credit would be nice, but is not neccesary.
 * Note        : This is NOT a complete style, but I don't have enough time to change everything. 
                 So, it might be slightly ugly/incomplete in places, but at least it's not bright white anymore. 
 * Update      : Again - not complete. Quite a big CSS file and not enough time to wade through it all in one day ; correct as needed please. CB
 
 */


/*body*/

body, element.style, .ad
{background: #161410 !important; color:#999 !important;}

/* white text */
 .module_title, label, .useract_toolbar ul li a, .score, .votes, .rank, .item, .item_detail, .browse_box span, .module .header .module_title, .module .header .module_title a, .board, .messages, .top_rank .body .sub_title, #todays_galleries .body .title span , #user_console .header .module_title a, .paginated_nav .pag_wrap span, .paginated_nav .pag_wrap .jump_links, #favorite_shows .subheader ,#content_nav li.on ul li a,.paginated_nav .pag_wrap ul li, #coming_up.module .header .module_title, #top_news .header .module_title, .listings_grid .body .content ul li .wrap li .episode, .new_dvds .body ul li .title
{color: #fff !important;}
 
/*black text*/
#content #top .content_title h2, .top_rank .body .score  
{color: #000 !important;}
 
#content #top, #content .col_wrap {background:#161410 !important; }

/* EYEBROW is the really small band on the top of the page */
#eyebrow { background:#161410 !important ; color:#ccc;}
#eyebrow a {color:#ccc;}
#bg_top { background:#161410 !important ;}

/* HEADER this is the main TV.com menu - adjust border color*/
#header {border-bottom:3px solid #161410 !important;}

/* USER PROFILE/CONSOLE, this is the thing in the upper right corner with your icon and name */
#user_console {background:#161410 !important ;}
#user_console .header .wrap {background:#161410 !important ;}
#user_console .body {background:#161410 !important ; color:#000;}
#user_console .body .basic_info .status span{background:#161410 !important ;color:#fff !important;}
#user_console .body .basic_info .msgs {background:#161410 !important ;}

/* LAYOUT */
#content {background:#161410 !important ;}

/* GENERIC MODULES */
.module {background:#161410 !important ;}
.module .header .module_title {color:#000 !important;}
.module .header .module_title a {color:#000 !important;}

/* COMMUNITY MODULES */
.module.community .header .wrap {background:#161410 !important ;}
.module.community .header .module_title {color:#fff !important;}
.module.community .header .module_title a {color:#fff !important;}
.module.community .header span, .module.community .header span a {color:#fcff00;}
.module.community .header .icon {background:#161410 !important;}

/* ACTION DIALOG BUBBLE */
.module .header .wrap .action_box {background:#161410 !important;}
.module .header .wrap .action_box .wrap {background:#161410 !important;}
.module .header .wrap .action_box .wrap .wrap {background:#161410 !important ;}

/* MINI COL SPONSORED MODULES */
.module.green .header .wrap {background:#161410 !important;}
.module.pink .header .wrap {background:#161410 !important;}
.module.green .header .module_title,.module.green .header .module_title a {color:#fff !important;}
.module.pink .header .module_title,.module.green .header .module_title a {color:#fff;}
#content #top .content_title h2, #content #top .content_title h2 a.forum_head {color:#fff !important;}
#content #top .content_title h2 span, #content #top .content_title h2 span a.forum_head {color:#666;}

/* LISTINGS MODULE */
#listings .header .wrap {background:#161410 !important;}

/* TOP NEWS MODULE */
#top_news .body {background:#161410 !important ;}
#top_news .thumb span, #top_news .thumb span a {background:#fcfc00; color:#fff !important;}

/* COMMUNITY MODULE - TOP MEMBERS */
#top_members .thumb {background:#161410 !important ;}
#top_members .body ul li .wrap .emblem {background:#eee;}

/* COMMUNITY - TOP FORUM TOPICS */
.related_forum_topics .body ul li {background:#161410 !important  ;}

/* INFO CONTENT CONTAINER MODULE */
#info_block .body {background:#161410 !important ;}

/* SHOWCASED (SIDE) PHOTO GALLERIES MODULE */
.showcased_galleries {background:#161410 !important;}

/* NEW PHOTOS MODULE */
.new_photos .body ul.gallery_titles li .title .on {color:#fff !important;}

/* STORY TOOLBAR */
#story_toolbar_wrap {background:#161410 !important ;}

/* inbox PM black text on whiet BG */
#popup #content {background: #FFF !important;color: #000 !important;}
#popup {background: #FFF !important; }
.m-5 {background: #FFF !important;}

/* FD TOPSLOT MODULE */
#fd_carousel .body {background:#161410 !important ;}

/* WHAT'S NEW MODULE */
#whats_new .header {background:#161410 !important ;}

/* dark brown border around rank numbers */
#top_shows .body li .rank {border:solid #161410 !important;}

/* SHOWSPACE: PHOTO STREAM */
#show_photo_stream .body .thumb_wrap {background:#161410 !important ;}

/* SHOWSPACE: EPISODE TABLE */
#eps_table .body table th {background:#161410 !important;}
#eps_table .body table td {background:#161410 !important;}

/* SHOWSPACE: EPISODE DOWNLOADS */
#eps_downloads table td {background:#161410 !important;}

/* SHOWSPACE: EPISODE */
#indepth_block .body .ep_tabs ul li.on a {background:#161410 !important ; color:#000;}

/* =PHC
------------------------------------------------------------------------------*/
#content #top .gallery_title h2 {color:#fff !important;}


/* =VHC
------------------------------------------------------------------------------*/
#content #top .video_title h2 {color:#fff !important;}

.browse_videos .header .wrap{background:#161410 !important; }
/* END =VHC
------------------------------------------------------------------------------*/

/* FORUM/BOARD LISTING MODULE */
.forum_list .body ul li .wrap, .forum_messages .post {background:#fff !important;}

/* NEWBIES SPOTLIGHT MODULE */
#newbies_spotlight .header {background:#161410 !important ;}

/* TOTAL SUBMISSIONS MODULE */
#total_submissions .body {background:#161410 !important;}

/* REVIEW OF THE DAY MODULE */
#featured_review .score_box {color:#fff !important;}
#featured_review .body .title span {color:#fff !important;}


/*------------- FORUM MESSAGE LIST/MESSAGE PREVIEW ---------------*/

.forum_messages .thumb {background:#161410 !important ; }
.forum_messages .body ul li .profile .wrap .msg_date {background:#161410 !important ;}
.forum_messages .action_panel .action{background:#161410 !important ;}
.forum_messages .action_panel .action.quick_reply{background:#161410 !important; }
.forum_messages .action_panel .action.quick_quote{background:#161410 !important; }
.forum_messages .action_panel .action.reply{background:#161410 !important ; }
.forum_messages .action_panel .action.quote{background:#161410 !important;}

/* nested replies */
.forum_messages table td.quote{background:#161410 !important ;}

/* WYSIWYG */
.forum_post .action_panel .action{background:#161410 !important ;}

/* FORUM SEARCH */
.forum_search form .text_input .text_field_wrap {background:#161410 !important ;}

/* FORUM SEARCH RESULTS */
.forum_search_results th {background:#161410 !important ;}

/* FORUM PAGINATION */
.forum_pagination.top{background:#161410 !important; }
.forum_pagination.bottom{background:#161410 !important ; }

/* NEWS CONTAINER - HUB & STORY PAGE */
.news_container .body {background:#161410 !important ;}
.news_container .body.last {background:#161410 !important ;}

/* NEWS STORY MODULE - HUB & STORY PAGE */
.news_story .body .title {color:#fff !important;}

/* FEATURE SPOTLIGHT MODULE*/
#feature_spotlight .header .wrap {background:#161410 !important ;}


/* TOP USERNAME */
#content #top .content_title.profile h2 {color:#fff !important;}

/* USER MODULE */
#user .body .level {background:#161410 !important ;}

/* MY FEED MODULE */
#feed .body ul li {background:#161410 !important ;}

/* MY CONTRIBUTIONS MODULE */
#contributions .body ul li ,feed .body ul li {background:#161410 !important ;}

/* MY FRIENDS MODULE */
#friends .body ul li , feed .body ul li {background:#161410 !important ;}
#friends .body ul li.online ,feed .body ul li {background:#161410 !important ;}

/* MY FAVORITE SHOWS MODULE */
#favorite_shows .body ul li , feed .body ul li {background:#161410 !important ;}

/* ABOUT ME MODULE */
#about_me .body dt {color:#fff !important;}

/* MY RECENT REVIEWS MODULE */
#recent_reviews .body ul li , feed .body ul li {background:#161410 !important ;}
#recent_reviews .body ul li .wrap .thumb_box , feed .body ul li {background:#161410 !important ;}
#recent_reviews .body ul li .wrap .thumb_box .thumb_up ,feed .body ul li {background:#161410 !important ;}
#recent_reviews .body ul li .wrap .thumb_box .thumb_down ,feed .body ul li {background:#161410 !important ;}

/* RECENT VIDEOS MODULE */
#recent_videos .body .icon , feed .body ul li {background:#161410 !important ;}


/* SHOW/ACTOR UPDATES (DASHBOARD)*/
#updates .body li ,feed .body ul li {background:#161410 !important ;}
#blog_post.forum_post .body .preview .wrap {background:#161410 !important ; }
.listings_set_location .no_providers,.listings_set_location .body form {background:#161410 !important ;}

/* LISTINGS GRID */
.listings_grid .timeslot_nav .nav .prev_wrap a,.listings_grid .timeslot_nav .nav .next_wrap a {background:#161410 !important;}
.listings_grid .body .content .scroll_bar {background:#161410 !important ;}

/* LISTINGS BY NETWORK*/
.listings_network table td.show .wrap{background:#161410 !important ;}
.rankings .header .rankings_nav, .rankings .header.rankings_nav ul li.on{color:#fff !important;}
.rank, .ranking_table th.rank {color: #fff !important;}

/* TOP RANKING */
.top_rank .body .sub_title{color:#fff !important;}

/* RANKING TABLE */
.ranking_table th {color:#fff !important;}

/* BROWSE BOX */
.browse_box ul li.on a{color:#fff !important;}

/*--- JUST SHOWS HUB ----*/
.shows_new_videos .body .title {color:#fff !important;}
#show_hub_gumballs .header li.first {color:#fff !important;}

/* SEARCH */
#content #top {background:#161410 !important ;}
.search_container {background:#161410 !important ;}
.search_nav_tabs ul li.on {background:#161410 !important ;}

/* MODERATION: DRILLDOWN USER SUBMISSIONS */
.mod_user_submitted .body li .wrap .input_wrap {background:#161410 !important ;}
}

/* Font Size!! */

#about_me .body .info span, 
#about_me .body dd span,
#about_me .body dl.my_story dd,
#about_me .body dl.my_story dt,
#about_me .body dt,
#blog_post.forum_post .body form input,
#blog_post.forum_post .body .preview p,
#blog_post.forum_post #topic_title,
#cast_sort_form .subheader .wrap .module_title,
