.forum_post form label,
/*.forum_search_hub .header,*/
/*.forum_search_hub form input.text_field,*/
.forum_thread_list_key span,
.forum_thread_list td.posts .total,
.galleries .body .title,
.gumball_list .body .title,
.listings_network table td.time,
.listings_network table td.day .wrap,
.listings_network table th.day .wrap,
.listings_grid .body .timeslot_nav .timeslots ul li,
.listings_set_location .body .title,
.listings_settings_bar .body form fieldset select,
.media_filter .body .categories .tab,
.media_search form input.text_field,
.new_photos .body ul.gallery_titles li .title,
.news_archive .body .deck,
.news_stories .body ul li .info,
.news_stories .body ul li .title,
.news_story .body .details,
.news_story .body .meta .keywords,
.news_story .body .meta .related,
.photo_list .body .title,
.related_nav dt,
.search .body .deck,
.search_nav_tabs ul li,
.showcased_galleries .title,
.showcased_videos .body .title,
.user_submission .body .intro .intro_blurb,
.user_submission .body ul li .wrap .intro_blurb,
.user_submission .body ul li .wrap .label_wrap label,
.videos .body .title ,
#community_signup .footer a, 
#most_popular_boards .body ul li .wrap .info span, 
#total_submissions .header span, 
#updates .body li .wrap .title span,

.comments .body .info,
.comments .misspelled dt, 
.module .subheader, 
.news_stories .body ul li .timestamp, .paginated_nav, .rankings .footer a, .stats_scores .body .stats_wrap .info li, .stats_scores .body .stats_wrap .score span, .rating_slider .user_score .description, .single_review form select, .top_rank .body .more, .top_rank .body .sub_title, .user_reviews .header .wrap .add_link, .user_reviews_list form select, .user_submission .body .errors li
{font-size: 1.4em !important; line-height: 1.4em !important;}

.deck
{font-size: 1.2em !important; line-height: 1.2em !important;}

#listings .body li .title, #listings .body li .title a, #listings .body li .info, #fd_poll .body table td.answer
{font-size: 1.1em !important; line-height: 1.2em !important;}

#form {height: auto !important;}

#my_shows_jump_list
{font-size: 0.8em !important; line-height: 1.2em !important;}

#eyebrow, #sky_layout .mini #side,
.ad {display:none !important;}

#about_me .thumb img {
display: block;
height:auto !important;
width:auto !important;

