css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("surrenderat20.net") {

    body
    {
        background-color: #171717 !important;
    }
    
    .news-content 
    {
        background: #222 !important;
        color: #ddd !important;
        
    }



/* Courtesy of @ARJ234 */
/* BEGIN */

 /* Change featured content box */
#HTML2 .widget-content, #HTML5 .widget-content, #HTML6 .widget-content, #HTML8 .widget-content {
background: #222;

}

/* Change seperator boxes (ad space?) */
#HTML1, #HTML3 {
background: #222;

}

/* Change the popular posts side-bar background and link color */
.PopularPosts .widget-content {
background: #222;

}

/* These are also global link color changes */
A:link{
color:#E07407 !important;
}

a:visited{
color:#67416D !important;
}
/* End popular post sidebar fixes */

/* Change blog archive box background color */
#BlogArchive2_ArchiveList {
background: #222;

}

/* Change blog archive dropdown menu */
#BlogArchive2_ArchiveMenu {
background: #111111;
color: #cac8c4;
}

/* Change the search box background color */
input.gsc-input{
background: #111111;
}

/* Change the text color of the text a user types into the search box */
input, select, textarea{
color: #FFF !important;
}

/* Change the Read More link background */
.jump-link a {
background: #484848;
} 


/* Courtesy of @ARJ234 */
/* END */


}
