css:
@-moz-document url-prefix("http://www.ohmydollz.com/forum/") {
/*Images et css by QuentinChtl. Animations araignées by S9clay.*/
body {
  background : url("http://www.zupimages.net/up/14/43/zrzs.png") fixed no-repeat, url("http://zupimages.net/up/14/43/gk2k.png") fixed no-repeat !important ;
    background-size : auto, cover !important ;
  background-position : calc(0% - 50px) 40%, bottom left !important ;
  }  
body:hover {
  background-position : calc(100% + 50px) 40%, bottom left !important ;
  -ms-transition : background-position 3s linear ;
  -o-transition : background-position 3s linear ;
  -moz-transition : background-position 3s linear ;
  -webkit-transition : background-position 3s linear ;
  transition : background-position 3s linear ;
}
    
    
#contenu {
  background : url("http://zupimages.net/up/14/43/io9f.png") fixed no-repeat !important ;
  background-position : calc(100% + 50px) 75% !important ;
  }  
#contenu:hover {
  background-position : calc(0% - 50px) 75% !important ;
  -ms-transition : background-position 4s ease-out ;
  -o-transition : background-position 4s ease-out ;
  -moz-transition : background-position 4s ease-out ;
  -webkit-transition : background-position 4s ease-out ;
  transition : background-position 4s ease-out ;
  -ms-transition-delay : 2s ;
  -o-transition-delay : 2s ;
  -moz-transition-delay : 2s ;
  -webkit-transition-delay : 2s ;
  transition-delay : 2s ;
}  

/*Images*/
#head_forum {
  background: url(http://zupimages.net/up/14/43/77cs.png) !important;
   }


#footer_box {
  background-image: url(http://zupimages.net/up/14/43/l23w.png) !important;
  color: rgb(196, 236, 194);
   }

#brdmenu {
  background: url(http://www.zupimages.net/up/14/43/qda5.png) no-repeat !important;
   }

.pun TH {
    background: url(http://zupimages.net/up/14/43/igbm.png) !important;
    color: rgb(180, 00, 00);
   }

.pun H2 {
    background: url(http://www.zupimages.net/up/14/43/qda5.png) !important;
  color: rgb(196, 236, 194);
   }
  
