css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("digg.com") {

/*text&background*/

html, body, tbody, tr, td, div, blockquote, li, ul, h1, h2, h3, h4, h5, h6, p, .votes, .votes strong, .text-input, .post-comment-textarea, .post-comment-form, .active{
  color: #B2CCFF !important;
  }
  body{  background: #13161A url("http://i.imgur.com/zunAMnr.jpg") left top repeat !important;
}
  /*links*/
  .site-header {
  background: #13161A url("http://i.imgur.com/zunAMnr.jpg") left top repeat !important;
  }
  
a{
color: #5C7DBD !important;
}

/*highlight*/
.selected, .dropdown-option:hover, .dropdown-option.selected{
	background: #333333 !important;
}

/*header*/
.digg-header{
	background: #0A0A0F !important;
	}
.digg-header .nav-list.nav-utility .nav-user .nav-link{
	background: #0A0A0F !important;
	}
  .digg-story__image-img{
    border: 1px solid #12121C;
    border-radius: 20px;	
  box-shadow: 5px 5px 5px 5px rgba(0,0,0,0.75);
}
.grid-3col .grid-row {
    box-sizing: border-box;
    margin: 0 0 1px;
}

          
/*images*/
.story-image-img{
	border: 2px solid;
  border-radius: 25px;	
  box-shadow: 5px 5px 5px 5px rgba(0,0,0,0.75);
  }
img:hover{
 	border: 2px solid;
  border-radius: 25px;	
  box-shadow: 5px 5px 5px 5px rgba(0,0,0,0.75);
  animation: neon2 1.5s ease-in-out infinite alternate;
  filter: contrast(1.25) brightness(1);
}
 @-webkit-keyframes neon2 {
  from {
    box-shadow: 0 0 1px #203042, 0 0 2px #203042, 0 0 3px #203042, 0 0 5px #228DFF, 0 0 8px #228DFF, 0 0 13px #228DFF, 0 0 21px #228DFF, 0 0 34px #228DFF;
  }
  to {
    box-shadow: 0 0 2px #203042, 0 0 3px #203042, 0 0 5px #203042, 0 0 8px #228DFF, 0 0 13px #228DFF, 0 0 21px #228DFF, 0 0 34px #228DFF, 0 0 55px #228DFF;
  }
}
#digg-alerts-container.alerts-enabled {
    border: 1px solid #DDD;
    width: 800px;
    float: left;
    display: block;
    max-height: 200px;
    margin-bottom: 26px;
    overflow: auto;
}
 .grid-3col .marquee .story-container {
    height: 361px;
    margin: 17px 0px 0px;
    padding: 0px 0px 17px;
    border-top: 0px none;
    background: #1A1B24 none repeat scroll 0% 0%;
}
  
/*article*/
  .onboarding{
	display: inline-block;
	max-height: 300px;
    
  }
  .story-container.digg-story-el.hentry.entry{
    padding: 5px;
    margin: 5px;
	border: 1px solid #12121C;
    border-radius: 20px;	
  box-shadow: 5px 5px 5px 5px rgba(0,0,0,0.75);

}
  .story-container.digg-story-el.hentry.entry:hover{
    padding: 5px;
    margin: 5px;
	border: 1px solid #12121C;
    border-radius: 20px;	
  box-shadow: 5px 5px 5px 5px rgba(0,0,0,0.75);

}
   .grid-3col .marquee .story-container .story-image {
    float: left;
    width: 800px;
    height: 360px;
    margin: 0px 2px 7px 0px;
    border-radius: 20px;
         background: #121217 none repeat scroll 0% 0%;
  
}
.story-content{
    padding: 1px 1px 1px 5px;
    margin: 1px 1px 1px 5px;
	width: 97%;
    height: 100px;
    overflow: auto;
    border-radius: 20px !IMPORTANT;

  }
  .story-container .story-kicker, .modal--digg-deeper__shared-by {
    color: #555;
    font: 700 0.688em/1.091em "lft-etica","Helvetica Neue",Helvetica,Arial,sans-serif;
    letter-spacing: 1px;
    margin: 0px 0px 0.364em;
    padding-left: 10px;
}
  .grid-3col .story-container, .story-grid-3col .story-container {
    padding: 17px 17px 34px;
    margin: 0px;
    min-height: 286px;
    position: relative;
}
  .popular-videos .story-container .story-title .story-link, .popular-videos .digg-story--deeper__sharer__name-link {
    color: #FFF;
    padding-left: 5px;
}
#digg-alerts-container .widget-hdr {
    background: #12121A none repeat scroll 0% 0%;
    border-bottom: 1px solid #EBEBEB;
    border-radius: 3px 3px 0px 0px;
    margin: 0px;
    padding: 6px 17px 5px;
    top: 0px;
    z-index: 3;
}
  .story-header{
        border-radius: 20px !IMPORTANT;
  }
.story-title, .entry-title{        
  border-radius: 20px !IMPORTANT;
  }
  .story-actions{
	padding: 1px 10px;
	margin: 1px 10px;
  width: 90%;
  float: right;
}

#popular-videos{
	background: #12121C !important;
	max-height: 400px;
  	padding: 1px 1px;
}
#popular-videos > div > div > article.story-container > div.story-content{
	width: 200px;
  	height: 200px;
}
.header{
  max-height: 10px;
  padding: 1px 1px;
}
.hed{
	background: #12121C !important;
}
.ftr{
	background: #0A0A0F !important;
}

.overflow-wrap{
	max-height: 280px;
}
.content{
	max-height: 280px;
}
  
  #popular-videos > div > div > article.story-container{
	max-height: 180px;  
  }
  
  #popular-videos > div > div > article.story-container > div.story-content{
	max-height: 80px;  
  }


.popular-upcoming-wrap{
	background: #12121C !important;
  	padding: 1px 5px;
}
.story-meta{
float: right;
}


#popular-stories > header{
	height: 30px;
  margin: 3px;
}
#popular-stories{ 
  width: 60%;
  padding: 0;
  margin: 0;
}

#popular-stories > div > div > article.story-container > div.story-content{
	background: #12121C !important;
  	padding: 5px 1px;
	max-height: 120px;
	display: block;  
  width: 320px;
}
#popular-stories > div > div > article.story-container > div.story-image{
	padding: 15px 0 0 0;
	background: #12121C !important;
}

#popular-stories > div > div > article.story-container{
	background: #12121C !important;
  	padding: 1px 1px;
}
#popular-stories > div > div > article.story-container > div.story-content > header > div.story-kicker{
  	background: #12121C !important;
}
#popular-stories > div > div > article.story-container > div.story-content > header > h2 {
  	background: #12121C !important;
  padding: 2px 2px;
        border-radius: 20px !IMPORTANT;
}
#popular-stories > div > div > article.story-container > div.story-content > header > div.story-meta{
  	background: #12121C !important;
}


#upcoming-stories > div > article.story-container > ul.story-actions{
  	background: transparent !important;
}
#upcoming-stories{
  width: 150px;
 padding: 0;
  margin: 0;
	float: left;
}
#upcoming-stories > div > article.story-container > div{
  width: 250px;
  height: 110px;
  padding: 0;
  margin: 0;
  	background: #12121C !important;
}
#upcoming-stories > div > article.story-container > div > header > h2 {
  	background: #12121C !important;
  padding: 8px 8px;
}

#upcoming-stories > div > article.story-container{
  	background: transparent !important;
	padding: 3px;
  	border: 1px solid;
    border-radius: 25px;	
  	width: 400px;
	margin: 3px 0;
}

/*video page*/

#content-main > div > section.video-story-container.stories-container > article > div.story-content{
 width: 95%;
  overflow: hidden;
}
#content-main > div > section.video-popular-stories > div > article.story-container.digg-story-el{
height: 140px;
  margin: 8px 2px;
}
#content-main > div > section.video-popular-stories > div > article.story-container.digg-story-el > div.story-content{
height: 70px;
}
#content-main > div > section.video-popular-stories > div > article.story-container.digg-story-el > div.story-image.story-image-thumb{
padding: 0;
  margin: 0;
}
.embedly-embed{
	border: 1px solid #12121C !IMPORTANT;
    border-radius: 25px !IMPORTANT;
}
.embed-code .embed-iframe{
	border: 1px solid #12121C !IMPORTANT;
    border-radius: 25px !IMPORTANT;
      	box-shadow: 2px 2px 3px #000;

}
#embed-code{
	border: 1px solid #12121C !IMPORTANT;
    border-radius: 25px !IMPORTANT;
  box-shadow: 5px 5px 5px 5px rgba(0,0,0,0.75);

}
}
