css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.powanet.org/hallodollz/") {    
  
    
body {
  background : url("http://www.zupimages.net/up/14/43/czig.png") fixed no-repeat, url("http://zupimages.net/up/14/43/gk2k.png") fixed no-repeat !important ;
    background-size : auto, cover !important ;
  background-position : calc(0% - 200px) 100%, bottom left !important ;
  }  
body:hover {
  background-position : calc(100% + 200px) 40%, bottom left !important ;
  -ms-transition : background-position 4s linear ;
  -o-transition : background-position 4s linear ;
  -moz-transition : background-position 4s linear ;
  -webkit-transition : background-position 4s linear ;
  transition : background-position 4s linear ;
  transition-delay : 10s ;       
}
   
    
#lightIRC {
  min-width : 960px !important ;
  width : 75% !important ;
  background : url("http://www.zupimages.net/up/14/43/yvbq.png") fixed no-repeat, url("http://www.zupimages.net/up/14/43/czig.png") fixed no-repeat !important ;
  background-position : calc(100% + 200px) 75%, -2200px calc(100% + 500px) !important ;
  }  
    
#lightIRC:hover {
  background-position : -200px 5%, 960px -200px !important ;
  -ms-transition : background-position 6s ease-out ;
  -o-transition : background-position 6s ease-out ;
  -moz-transition : background-position 6s ease-out ;
  -webkit-transition : background-position 6s ease-out ;
  transition : background-position 6s ease-out ;
  -ms-transition-delay : 4s ;
  -o-transition-delay : 4s ;
  -moz-transition-delay : 4s ;
  -webkit-transition-delay : 4s ;
  transition-delay : 17s ;
}  
    
    
}
