css:
@-moz-document domain(www.tumblr.com) {

Body {

  background:url("http://i.imgur.com/QWxqQSx.png") no-repeat fixed !important;

  background-color: rgba(0,0,0, 1) !important;

  background-position: 0% 0% !important;

  background-size: cover !important;

}



div.l-header .tab.iconic a{
  background-color: rgba(186,0,0,.5) !important;
  border-radius: 20px !important;  
}

div.l-header-container { /*++*/
	background-color: transparent;
	box-shadow: none; /* else there will be a faint line at the bottom */
	position: absolute; /* let the header move with the page */
}

.compose-button {background: #ff4e4e !important;border-color: #ff4e4e !important;}


#xkit-control > a {
background-color:rgba(186,0,0,.5) !important;
border-radius: 20px !important;
}

#new_post_notice_container.tab_notice, .tab_notice {
color: #fff !important;
background-color: #f13d5f !important;
}
.mceContentBody {
  background: rgb(255, 255, 255) !important; 
  color: rgb(68, 68, 68) !important; 
}

#search_query {
    background-color: rgba(255,115,164,.4) !important;
}


#logo, #logo a {
  position: relative;
  z-index: 1;
  width: 270px !important;
  height: 76px !important;
  background-repeat: no-repeat;
  background: url("http://i.imgur.com/h3IJ7O0.png")!important;
}

.new_post_label .new_post_label_icon {
background-image: url('http://i.imgur.com/iJTu37Q.png') !important;
background-size: 530px 100px !important;
color: rgba(0,0,0,1) !important;
margin-left: -9px !important;
padding-left: 0px !important;
margin-top: 0px !important;
margin-right: 0px !important;
opacity:1 !important;
}

#post_buttons  {
background: url(http://i.imgur.com/iJTu37Q.png) center no-repeat;
      border-radius: 5px;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    -o-border-radius: 5px;

}


div.l-content #new_post_label_text i.icon_post_text{
  visibility: hidden !important; }

div.l-content #new_post_label_photo i.icon_post_photo{
  visibility: hidden !important; }

div.l-content #new_post_label_quote i.icon_post_quote{
  visibility: hidden !important; }

div.l-content #new_post_label_link i.icon_post_link{
  visibility: hidden !important; }

div.l-content #new_post_label_chat i.icon_post_chat{
  visibility: hidden !important; }

div.l-content #new_post_label_audio i.icon_post_audio{
  visibility: hidden !important; }

div.l-content #new_post_label_video i.icon_post_video{
  visibility: hidden !important; }

#posts .post .post_content {
  background: transparent !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background: none;
-webkit-border-radius: 16px !important;
border-radius: 16px !important;
padding-top: 8px !important;
padding-bottom: 20px !important;
margin-top: 20px !important;
margin-right: -175px !important;
margin-left: 175px !important;
}


#posts .post {
  background: rgba(255,255,255,.8) !important;
  border-radius: 35px !important;
}


.post_notes{
  color: rgba(156,0,0,1) !important;
}



.popover_menu_item {
  background: rgba(255,255,255,1) !important;
  border-bottom: 1px solid #7f002d !important;
  
}


.selection_nipple:not([class="selection_nipple white"]) {

  border-bottom: solid 11px rgba(255,255,255,1)  !important;

}





.plexi.color.active.show {
  background-color: rgba(255,255,255,.5) !important;
}
.drop_target {
  background-color: rgba(255,255,255,.5) !important;
}
.drop_zone {
  border: solid 2px rgba(0,0,0,1) !important;
  border-radius: 2px !important;
}
#tumblelog_select > .txt {
  color: rgba(0,0,0,1) !important;
}
#tumblelog_select > .txt:hover,
.photo_field.drop_zone_container .drop_zone .url_button {
  color: rgba(255,255,255,1) !important;
}
.chrome.flat.close:hover,
.photo_field.drop_zone_container .drop_zone .url_button:hover {
  color: rgba(255,255,255,1) !important;
}
#tumblelog_select > .txt:hover:after,
.post_options_wrapper .cog:hover,
.chrome.white.options {
  opacity: 1 !important;
}
#tumblelog_select > .txt:after,
.post_options_wrapper .cog {
  opacity: 1 !important;
}







.post_content DIV:last-child,BLOCKQUOTE{border-color:rgba(255,0,0,.6)!important}


pre,pre a,pre SPAN,.quote_source pre,div pre,pre SPAN,pre a,CODE,pre small{background:rgba(255,255,255,.2)!important;color:rgba(0,0,0,1)!important;font:bold .9em "arial",consolas,courier!important;white-space:pre-wrap!important;white-space:-moz-pre-wrap!important;white-space:-pre-wrap!important;white-space:-o-pre-wrap!important;word-wrap:break-word!important}




.chat_line:nth-child(even),PRE{background-color:rgba(255,255,255,0)!important;
color:rgba(0,0,0,1)!important}


.answer_container,
.notes LI{background:rgba(255,255,255,.5)!important;
}
 




#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

  
background: rgba(0,0,0,0) !important;

}





li.selected, li.recessed, a.active, div.post_filter_tabs a {

  background: none !important;

}

.controls_section.user_list li .follow_list_item_blog.has_follow_button:before{
visibility: hidden !important;
}

.controls_section.user_list li .follow_list_item_blog .tumblelog_title{
color: rgba(186,0,0,1) !important;
}






.post.photo_post .photo, .post.photo_post .video_thumbnail {

 box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;

  border-radius: 12px !important;

}

.content.is_media, .mask.top, .mask.bottom {

  background: none !important;

}





#content_top.content_cap,

#content_bottom.content_cap {

  visibility: hidden !important;

}

div#edit_post_controls {

  top: 20px !important;

}

#dashboard_edit_post #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

  border-radius: 14px !important;

}

#auto_pagination_loader{

background: rgba(255,255,255,.5) !important;

border-bottom: 0px solid #2A3B52 !important;

border-top: 2px solid #ff0042 !important;

}




.post_content,
.new_post_label,

#right_column > UL:first-child > LI:first-child > A > SMALL > DIV,


.post_controls A,

.post_info,

.post_info A,

.footer_links.with_tags.with_source_url A,

.footer_links A,

.footer_links,

.block,


#dashboard_controls A,

#dashboard_controls DIV SPAN,

.add_and_remove,

.notification.single_notification,

.notification,

.notification .hide_overflow A,

.notification.last_notification,

.notification.last_notification A,

.answer_container SPAN,

.notes,

.notes LI,

.notes_hide_link A,

.read_more_container,

#footer, #footer DIV A,

.post_question_asker,

.source_url,

.label_and_follow,

.follow, #search_query, #search_controls_button,

.posts, .followers, .messages, .drafts, .queue, .flat.no_hover DIV,

A[class^="category"],

a:link,

a[href^="http"],

a[href^="/edit"],

a[href^="/likes"],

a[href^="/new"],

a[href^="/tagged"],

a[href^="/login"],

a[href^="/docs"],

a[href^="/explore"],

a[href^="/mega-editor"],

.count, .customize, .settings,

a span[id^="note_link"],

.answer_container,

.no_posts_found,

.following, H1[id="page_heading"], div.l-content P, H1, p SMALL,

.notes_loader,

.small_links A .hide_overflow, .sorry P, .cell.lead_editor, .sidebar_nav_item A, .tag,

 H2, #posts .post .post_controls .reblog_count span {

  color: rgba(186,0,0,1) !important;

}

a:link:hover,

A[href^="http"]:hover,

a[href^="/edit"]:hover,

a[href^="/likes"]:hover,

a[href^="/new"]:hover,

a[href^="/tagged"]:hover,

a[href^="/customize"]:hover,

a[href^="/explore"]:hover,

a[href^="/mega-editor"]:hover,

a[id^="ask_answer"]:hover,

a span[id^="note_link"]:hover,

.block_link:hover,

.following:hover, .add_and_remove:hover, #search_query:hover, #search_controls_button:hover, .settings:hover, .posts:hover, .followers:hover, .messages:hover, .drafts:hover, .queue:hover,

SMALL, .text_field.big.wide, #crushes .count, #l10n_sorry, #footer DIV A:hover, #footer DIV STRONG:hover,

BUTTON[type="submit"]:hover, .small_links A .hide_overflow:hover, .block:hover, #signup_forms_submit:hover, .sidebar_nav_item A:hover,

#posts .post .post_controls .reblog_count span:hover {

  color: rgba(255,146,173,1) !important;

}

.source_url_gradient, #toggle_reasons {

  display: none !important;

}

.notification.single_notification A,

.notification.first_notification A,

.notification A,

.notification.last_notification A,

.notification .hide_overflow A {

  border-bottom: solid thin rgba(64,64,64,1.0) !important;

}

.post_content P A,

.post_title A {

    color: rgba(163,0,0,1) !important;

}

.tag {color:rgba(163,0,0,1)!important;
}


.post_permalink {opacity:.8!important;
  background: #ff003c !important;
 border-radius: 15px !important;
  } 

.post_full .post_permalink:before {
border-right: 15px transparent !important;
}

#tumblr_radar {display: none !important;}


#sidebar_footer_nav {
 display: none !important;
}

#right_column .controls_section li a,

#right_column .controls_section li a .count {

  color: #b0012f !important;
  background-color: rgba(255,255,255,0)!important;
  border-radius: 10px !important; 
}



#right_column .controls_section li a:hover {

  color: #b0012f !important;
  background-color: rgba(255,255,255,.8)!important;
  border-radius: 10px !important;
}



.notification.single_notification,

.notification.first_notification,

.notification.last_notification,

.notification {

  background: none !important;

  border: none !important;

}

.notification.single_notification,

.notification.first_notification,

.notification.last_notification,

.notification,

.notification.single_notification A,

.notification.first_notification A,

.notification A,

.notification.last_notification A,

.notification .hide_overflow A {

  color: rgba(255,255,255,1) !important;

}

.notification.single_notification A,

.notification.first_notification A,

.notification A,

.notification.last_notification A,

.notification .hide_overflow A {

    border-bottom: solid thin rgba(101,28,13,0) !important;

}

.notification.single_notification A:hover,

.notification.first_notification A:hover,

.notification A:hover,

.notification.last_notification A:hover,

.notification .hide_overflow A:hover {

  color: rgba(255,0,0,0) !important;

}



ol#posts li.notification.alt {

   background: rgba(255,184,210,.6) !important;



}

.note_item {
background-color:  rgba(255,92,131,.3)!important;
border-radius: 10px !important;
border: 0px solid !important;
border-color: #fff!important;
}




#content_top, #content_bottom{display:none !important;}





ol#posts li.notification {

   background: rgba(255,255,255,.4) !important;

    border-bottom: 0px solid #ff254e !important;



}







#left_column #posts .notification a {

                        color:#FFFFFF !important;

                        border-bottom:0px !important;

                    }




div#right_column,

div#right_column:after {

  background: none !important;

}



.mceContentBody {

  color: rgba(0,0,0, 0) !important;

}
}

@-moz-document url("http://www.tumblr.com/dashboard/iframe") , url("https://www.tumblr.com/dashboard/iframe") {
body {background:none!important;}
}
