.module_title {
  color:#dbdbdb!important;
  }
.field_row_label_inner, .image_edit_button, .field_row_color .field_row_input_inner, .field_row_select.large_and_in_charge .flat_select, .field_row .binary_switch_checkbox, .field_row_input, .field_row_input_prefix_inner, #theme_categories_menu, a.theme_garden_promo:hover, a.theme_garden_promo, .secondary_bar .ace_search.right, .secondary_bar .ace_search, .popover_menu_item:hover, .upload_static_file_container .header, .upload_static_file_container .uploaded_file, .sortable_item:hover  {
