  background:#212121!important;
  color:#939393!important;
  }
.upload_static_file_container .title {
  color:#dbdbdb!important;
  }        
.file_upload.dropzone .dropzone_label, .file_upload.dropzone .dropzone_box, .upload_static_file_container .uploaded_file .asset_filename {
  color:#c8c8c8!important;
  border-color:#191919!important; 
  }
.dropzone_label .small, .upload_static_file_container .asset_replace, .upload_static_file_container .file_actions .text_button {
  color:#939393!important;
  }        
.panel_popover_content, .image_picker .menu_option {
  background:#343434!important;
  color:#c8c8c8!important;
  }
.image_picker .menu_option:hover {
  background:#212121!important;
  color:#939393!important;
  }
.color_editor .color_palette .pagination a.dot {
  background:#343434!important;
  border-color:#212121!important;
  }
.image_picker_popover:after, .color_picker_popover:after {
  border-right-color:#343434!important;
  }
.sortable_item .name {
  color:#939393!important;
  }
.input_prefix, #page_request_uri, #page_title {
  color:#c8c8c8!important;
  }
.fieldset {
background:none!important;
  }
.panel .title_header {
  background:#191919!important;
  }
.panel .content_container {
  background:#212121!important;
  }
.button.green:active, .button.green:not(.disabled) {
  background:#8585ff!important;
  border-color:#8585ff!important; 
  color:#eee!important;
  }
.button.disabled.green {
  background:#9999ff!important;
  border-color:#9999ff!important; 
  color:#eee!important;
  }
.button.blue:not(.disabled) {
  background:#FF8585!important;
  border-color:#FF8585!important; 
  color:#eee!important;
  }
.button.disabled.blue {
  background:#FF9999!important;
  border-color:#FF9999!important; 
  color:#eee!important;
  }
.ace-tumblr .ace_gutter-layer {
  color:#939393!important;
  }
.ace_tumblr.ace_tag.ace_begin, .ace_tumblr.ace_tag.ace_end {
  color:#D699FF!important;
  }
.ace_tumblr.ace_tag {
  color:#FFB5D3!important;
  }
.ace-tumblr .ace_comment {
  color:#FF6666!important;
  }
.ace_tumblr.ace_prefix {
    color:#66E0A3!important;
    }
.ace_tumblr.ace_prefix {
    color:#66E0A3!important;
    }
.ace-tumblr .ace_string {
    color:#CDB4FF!important;
    }
.ace_attribute-name {
    color:#66FFE0!important;
    }
.ace_css_string, .ace_css_variable, .ace_css_string, .ace_css_constant {
    color:#FF9966!important;
    }
.ace-tumblr .ace_type {
    color:#80E6FF!important;
    }
.ace-tumblr .ace_constant {
    color:#FF5C9D!important;
    }
.ace-tumblr .ace_constant.ace_numeric {
    color:#E06666!important;
    }
.ace-tumblr .ace_keyword {
    color:#FFFFCC!important;
    }
