css:
@-moz-document domain("u-labs.de") {
/* bg: #202020 */
/* gray: #383838 */
/* pink: #a98ce3 */

/* ** body bg ** */
.st-content-inner {
	background-color: #202020 !important;  
}

/* ** links ** */
body a {
	color: black !important;
}

body a:hover {
	color: #313639 !important;
}


/* ** threadbit ** */

.threadbit:nth-child(2n+1) {
  background: rgba(255,255,255, 1) !important;     
}

.threadbit:nth-child(2n) {
  background: rgba(255,255,255, 0.8) !important;      
}

/* ** top nav ** */

/* top nav bg */
.bm-container {
	background-color: #383838 !important;    
}

/* buttons - ernsthaft? für jeden scheiß button eine eigene klasse? */
.facebook, .facebook-sb, .twitter, .twitter-sb, .google-plus, .google-plus-sb, .pinterest, .pinterest-sb, .instagram, .instagram-sb, .linkedin, .linkedin-sb, .yahoo, .yahoo-sb, .youtube, .youtube-sb, .vimeo, .vimeo-sb, .login-toggle, .search-toggle, .side-menu-toggle {
	background-color: #383838 !important;  
	color: white !important;
	border: 0 !important;
}

.facebook:hover, .facebook-sb:hover, .twitter:hover, .twitter-sb:hover, .google-plus:hover, .google-plus-sb:hover, .pinterest:hover, .pinterest-sb:hover, .instagram:hover, .instagram-sb, .linkedin, .linkedin-sb, .yahoo, .yahoo-sb:hover, .youtube:hover, .youtube-sb:hover, .vimeo:hover, .vimeo-sb:hover, .login-toggle:hover, .search-toggle:hover, .side-menu-toggle:hover {
	background-color: #7c7c7c !important;  
	border: 0 !important;
	color: #f9f9f9 !important;
}

/* schrecklicher radius weg damit */
.top-nav > ul > li > a,
.top-nav>ul>li.top-nav-mega:hover>ul {
    border-radius: 0 !important;
}

.top-nav ul > li a {
  color: white !important;
}

.top-nav > ul > li:hover > a {
    background: #7c7c7c !important;
}

.top-nav-mega-1 .mega-news-item:hover {
	background-color: rgba(255,255,255, 0.05) !important; 
}

.top-nav-mega-1,
.top-nav > ul > li > ul {
	background-color: rgba(32,32,32, 0.95) !important; 
	box-shadow: 2px 0 5px #202020 !important;
}

.top-nav > ul > li > ul > li > a,
.top-nav > ul > li > ul > li > a:hover{
	background-color: inherit;
	border-top: 1px solid rgba(255,255,255, 0.5);
	border-bottom: 0;
}

.top-nav > ul > li > ul > li > a:hover {
	background-color: rgba(255,255,255, 0.1);
}

/* ** sidebar ** */
nav#menu-1 h2 a {
	color: #a98ce3 !important;
}

nav#menu-1 h2 a:hover {
	color: #b89eed !important;
}
	
nav#menu-1 h2::after {
	content: "u-hacks höhöh ~ patlux";
	color: gray !important;
	font-size: 11px;
	display: block;
	margin-top: -9px;
}
	
nav#menu-1 ul > li a {
	padding: 15px;
	text-decoration: none;
  	color: white !important;
}

/* ** breadcrumb ** */
.breadcrumb {
	background-color: #444444;
	border-radius: 0 !important;
	color: white;
}

.breadcrumb .navbithome {
	display: none;
}

.breadcrumb .floatcontainer > li {
	padding: 0 0 5.5px 0;
}

/* ** ------ ** */
/* ** THREAD ** */

/* ** latest posts ** */
#vsa_fh_stats {
	
}
	
#vsa_fh_stats td a {
	
}
	
#vsa_fh_stats a {
	color: white !important;
}


#vsa_fh_stats td.blocksubhead {
	background-color: #727272;
}

#vsa_fh_stats td {
	background-color: #4e4e4e;
}
	
	
/* ** blockhead ** */	
.blockhead {
	display: none;
}

#login-container {
  background: #1F1F1F !important;   
  border: 1px solid black !important;
  box-shadow: none !important;
  border-radius: 0 !important;
  
  opacity: 0;
}

.login-effect {
  	color: white !important;
    background-color: #1F1F1F !important;
  
  	opacity: 0.95 !important;

}
  
.login-effect a {
  	color: white !important;
}
    
.login-effect a:hover {
  	color: white !important;
}   
  
    
.big-blue-button {
    background-color: #383838 !important;
    border-radius: 1px !important;

}


.search-effect {
  background: #1F1F1F !important;   
  border: 1px solid black !important;
  box-shadow: none !important;
  border-radius: 0 !important;
      
}
}
