css:
@-moz-document url-prefix("http://www.twitch.tv/vinesauce/chat") {

body div.ember-chat-container, body .ember-chat .chat-hidden-overlay, .ember-chat .chatters-view{
color: #eee !important;
background: #222222 !important;
}

body {
font-family:'Open Sans', Arial, sans-serif !important;
}

.ember-chat .ember-list-item-view a{
color: #09f !important;
}

body .ember-chat .chat-interface .textarea-contain textarea{
color:#eee !important;
background: #1e1e1e !important;
 outline: none;
  border:none;
     box-shadow:none;
}

body .ember-chat .chat-interface .textarea-contain textarea:focus {
  outline: none;   
  border:none;
     box-shadow:none;
     background:#171717 !important;
}
        
body .button.glyph svg path, .button.glyph-only svg path{
fill:#444444 !important;
  -webkit-transition: all 250ms ease;
-moz-transition: all 250ms ease;
-o-transition: all 250ms ease;
transition: all 250ms ease;
}

body .button.glyph:hover svg path, .button.glyph-only:hover svg path{
fill:#0099ff !important;
}

input:focus, input:active, input:hover{
outline:none !important;
}

body .button.primary{
background:#444444 !important;
-webkit-transition: all 250ms ease;
-moz-transition: all 250ms ease;
-o-transition: all 250ms ease;
transition: all 250ms ease;
font-weight: 700 !important;
text-transform: uppercase;
}

body .button.primary:hover{
background:#09f !important;
}

body .ember-chat .chat-interface .textarea-contain .emoticon-selector-toggle svg path{
fill:#eee !important;
opacity:0.2;
  -webkit-transition: all 250ms ease;
-moz-transition: all 250ms ease;
-o-transition: all 250ms ease;
transition: all 250ms ease;
}

body .ember-chat .chat-interface .textarea-contain .emoticon-selector-toggle:hover svg path{
fill:#eee !important;
opacity:0.8;
}

body .ember-chat .chat-room-list, body .ember-chat .chat-settings, .dropmenu, .ember-chat .top-notification,.ember-chat .chat-commands-dropdown {
background:#171717 !important;
color:#999 !important;
}

body .ember-chat .chat-room-list a, body .ember-chat .chat-settings a{
color:#09f !important;
}

body .ember-chat .chat-room-list .room p.room-title{
  -webkit-transition: all 250ms ease;
-moz-transition: all 250ms ease;
-o-transition: all 250ms ease;
transition: all 250ms ease;  
color:#09f !important;
}

body .loading-mask{
  background-color:#222;
  opacity:0.8;
  height:100%;
  }
  
body .ember-chat .chat-room-list .room{
  -webkit-transition: all 250ms ease;
-moz-transition: all 250ms ease;
-o-transition: all 250ms ease;
transition: all 250ms ease;  
background:#171717 !important;
}

body .ember-chat .chat-room-list .room:hover{  
background:#09f !important;
}

body .ember-chat .chat-room-list .room:hover p.room-title{
  -webkit-transition: all 250ms ease;
-moz-transition: all 250ms ease;
-o-transition: all 250ms ease;
transition: all 250ms ease;  
color:#fff !important;
}

body .ember-chat .chat-messages .chat-line a{ 
color:#09f !important;
}

.ember-chat .top-notification.game-commands-notification{
  background-color:#09f !important;
              }
}
