css:
@-moz-document domain("inoagame.com") {
body, #body {
  background: #efefef;
}

div.corner {
    width: 55px;
    height: 71px;
}

div.sign-text	{
    display:none;
}

div.corner div.gold_member {
    width: 55px;
    height: 14px;
    background-size: 55px;
}

div.avatar {
    width: 55px;
    height: 55px;
}

div.avatar img {
    width: 100% !important;
    height: 100% !important;
}
    
div#my-name, div#my-avatar	{
  display: none;
}

object {
  position: absolute;
  left: -10000px;
  display: none;
}

img#tail, img#fog	{
  display: none;
}

div#header	{
  top: 50px;
}

div#topContainer	{
  height: 100px;
}

div#counter, div#pegi	{
  display: none;
}

div#options	{
  margin-left:40%;
  font-weight: bold;
}
}
