css:
@-moz-document domain("www.ynet.co.il") {
div[id="ads.top"],
font.text14 div[style="float: left;"],
#tbl_mt td:nth-of-type(6)
{
    display: none !important;
}

#divAreac + table > tbody > tr:first-child + tr,
#divAreac + table > tbody > tr:first-child + tr + tr,
#divAreac + table > tbody > tr:first-child + tr + tr + tr,
#divAreac + table > tbody > tr:first-child + tr + tr + tr + tr + tr,
#divAreac + table > tbody > tr:first-child + tr + tr + tr + tr + tr + tr + tr + tr + tr,
#divAreac + table > tbody > tr:first-child + tr + tr + tr + tr + tr + tr + tr + tr + tr + tr
{
    display: none !important;
}

.block.B2b {
  	width: 150px !important;
	visibility: hidden;
}


* {
	font-family: Arial, Arial Hebrew !important;
}

a.bluelink:link, a.bluelink:visited {
  font-size: inherit !important;
}

a.bluelink:visited {
  color:#808 !important;
}

a.smallheader:visited {
  color:#B3B3B3 !important;
}
}

@-moz-document url-prefix("http://www.ynet.co.il/articles/") {
div.block.B1.spacer > div.block.B1:nth-of-type(2),
div.block.B6 > div.block.B6,
div.block.B1.spacer,
div.element.B3.ghcite:nth-of-type(4) ~ *,
.element.B2b.ghcite.noBottomPadding ~ *,
#ww6s_Main
{
    display: none !important;
}

div.block.B4.spacer {
	float: right !important;
}

}
