css:
@-moz-document domain("wykop.pl") {
    /* Usunięcie z całego serwisu wszelkich oznak używania "transition": */ /* Niech wypierdalają po chleb albo na beton. */
    *, *:hover {
        transition: all 0s !important;
    }

    /* Strona główna: */
        /* Zmiana koloru tła całej strony głównej: */
        #site.m-reset-padding.m-reset-margin {
            background-color: #FFFFFF !important;
        }
    
        /* Belka nawigacyjna: */
            /* Zmiana koloru belki nawigacyjnej: */
            #nav.clearfix.m-reset-position {
                background: linear-gradient(#71858E, #7D909A) !important;
            }
    
            /* Zmiana koloru tła loga wykopu: */
            #nav.clearfix.m-reset-position > .wrapper.nav.color-bright > .clearfix.mainnav > .active > a{
                background: linear-gradient(#71858E, #7D909A) !important;
            }
    
            /* Dodanie nawiasu otwartego do liczby aktywnych znalezisk w Wykopalisku: */
            #nav  > .wrapper.nav.color-bright > .clearfix.mainnav > li > a > .mark-number::before {
                content: "(" !important;
            }
        
            /* Dodanie nawiasu zamkniętego za liczbą aktywnych znalezisk w Wykopalisku: */
            #nav  > .wrapper.nav.color-bright > .clearfix.mainnav > li > a > .mark-number::after {
                content: ")" !important;
            }
    
            /* Usunięcie tła z liczby aktywnych znalezisk w Wykopalisku: */
            #nav  > .wrapper.nav.color-bright > .clearfix.mainnav > li > a > .mark-number {
                background-color: transparent !important;
                font-weight: normal !important;
                font-size: 95% !important;
            }
    
        /* Belka "MÓJ WYKOP" na stronie głównej: */
            /* Zmiana koloru tła w "MÓJ WYKOP": */
            #site.m-reset-padding.m-reset-margin > .wrapper.clearfix.m-reset-padding > .grid-right.m-reset-float.m-reset-margin.m-reset-width.m-hide > #userPanel.r-block {
               background-color: #FFFFFF !important;
            }

            /* Zmiana pozycji napisu "MÓJ WYKOP": */
            #site.m-reset-padding.m-reset-margin > .wrapper.clearfix.m-reset-padding > .grid-right.m-reset-float.m-reset-margin.m-reset-width.m-hide > #userPanel.r-block > .ddC {
                background-color: #FFFFFF !important;
                text-align: left !important;
                font-weight: bold !important;
                font-size: 103.5% !important;
                height: 40px !important;
            }

            /* Zmiana pozycji rozwijanego napisu "wszystko": */
            #site.m-reset-padding.m-reset-margin > .wrapper.clearfix.m-reset-padding > .grid-right.m-reset-float.m-reset-margin.m-reset-width.m-hide > #userPanel.r-block > .ddC > a {
                position: absolute !important;
                top: 15px;
                text-align: right !important;
                right: 0px !important;
            }

            /* Zmiana pozycji rozwijanego pola pod napisem "wszystko": */
            #site.m-reset-padding.m-reset-margin > .wrapper.clearfix.m-reset-padding > .grid-right.m-reset-float.m-reset-margin.m-reset-width.m-hide > #userPanel.r-block > .ddC > .dropdown.fix-dropdown.right-position > div {
                position: absolute !important;
                right: 0 !important;
                top: 27px !important;
                width: 136%;
            }
    
        /* Blok sortujący znaleziska według najnowszych, aktywnych i ulubionych: */
            /* Zmiana tekstów obok napisu "STRONA GŁÓWNA" w taki sposób, aby ich pierwsze litery były napisane wielkimi: */
            #site > .wrapper.clearfix.m-reset-padding > .grid.m-reset-float > .grid-main.m-reset-margin > .nav.bspace.rbl-block > ul > li {
                text-transform: capitalize !important;
            }
    
            /* Zmiana koloru linka: */
            #site > .wrapper.clearfix.m-reset-padding > .grid.m-reset-float > .grid-main.m-reset-margin > .nav.bspace.rbl-block > ul > li.active > a {
                color: #3C5E6F !important;
                font-size: 110% !important;
                font-weight: bold !important;
                border-bottom: none !important;
            }
    
            /* Zmiana koloru tekstów obok napisu "STRONA GŁÓWNA" na pomarańczowy, gdy są nieaktywne: */
            #site > .wrapper.clearfix.m-reset-padding > .grid.m-reset-float > .grid-main.m-reset-margin > .nav.bspace.rbl-block > ul > li:not(.active) > a > span {
                color: #DF6737 !important;
                font-size: 90% !important;
            }
    
            /* Usunięcie z aktualnie zaznaczonego bloku z tekstem dekoracji: */
            #site > .wrapper.clearfix.m-reset-padding > .grid.m-reset-float > .grid-main.m-reset-margin > .nav.bspace.rbl-block > ul > li.active > a:hover {
                cursor: text !important;
            }
    
        /* Kolory tekstu na stronie głównej: */
            /* Kolor reklam z wykop marketu: */
            #dyingLinksBox > .link.dying-links.mark-bg.iC > .article.clearfix.preview > .lcontrast.m-reset-margin > .description > .fright.small > a {
                color: #DF6737 !important;
            }
    
            /* Kolor linków w opisie tekstu z "Wykop Sponsorowany": */
            #itemsStream > .link.iC > .article.clearfix.preview > .lcontrast.m-reset-margin > div > .sub-menu.inline-list.clear-top > li > a {
                color: #DF6737 !important;
            }

            /* Kolor linków w opisie tekstu, gdy nie jest najechany: */
            #itemsStream > .link.iC > .article.clearfix.preview.dC > .lcontrast.m-reset-margin > .fix-tagline > a {
                color: #6C808A !important;
            }
     
            /* Kolor elementów w opisie tekstu, gdy nie jest najechany: */
            #itemsStream > .link.iC > .article.clearfix.preview.dC > .lcontrast.m-reset-margin > .row.elements > a.affect {
                color: #DF6737 !important;
            }
    
            /* Kolor elementów w opisie tekstu, gdy się na nie najedzie: */
            #itemsStream > .link.iC > .article.clearfix.preview.dC > .lcontrast.m-reset-margin > .row.elements > a.affect:hover {
                color: #3C5E6F !important;
            }

            /* Kolor najechanych linków w opisie tekstu z "Wykop Sponsorowany": */
            #itemsStream > .link.iC > .article.clearfix.preview > .lcontrast.m-reset-margin > div > .sub-menu.inline-list.clear-top > li > a:hover {
                color: #3C5E6F !important;
            }

            /* Kolor strzałek w reklamach z wykop marketu: */
            #dyingLinksBox > .link.dying-links.mark-bg.iC > .article.clearfix.preview > .lcontrast.m-reset-margin > .description > .fright.small > .button.mikro.prev-link,
            #dyingLinksBox > .link.dying-links.mark-bg.iC > .article.clearfix.preview > .lcontrast.m-reset-margin > .description > .fright.small > .button.mikro.next-link {
                color: #3C5E6F !important;
            }

        /* Linki: */
            /* Zwykłe linki: */
            #itemsStream > .link.iC > .article.clearfix.preview.dC > .lcontrast.m-reset-margin > h2 > a,
            #itemsStream > .link.iC > .article.clearfix.preview > .lcontrast.m-reset-margin > h2 > a,
            #dyingLinksBox > .link.dying-links.mark-bg.iC > .article.clearfix.preview > .lcontrast.m-reset-margin > h2 > a {
                color: #DF6737 !important;
            }
        
           /* Linki, na które się najeżdża: */
            #itemsStream > .link.iC > .article.clearfix.preview.dC > .lcontrast.m-reset-margin > h2 > a:hover,
            #itemsStream > .link.iC > .article.clearfix.preview > .lcontrast.m-reset-margin > h2 > a:hover,
            #dyingLinksBox > .link.dying-links.mark-bg.iC > .article.clearfix.preview > .lcontrast.m-reset-margin > h2 > a:hover {
                color: #3C5E6F !important;
            }

            /* Linki odwiedzone: */
            #itemsStream > .link.iC > .article.clearfix.preview.dC > .lcontrast.m-reset-margin > h2 > a:visited,
            #itemsStream > .link.iC > .article.clearfix.preview > .lcontrast.m-reset-margin > h2 > a:visited,
            #dyingLinksBox > .link.dying-links.mark-bg.iC > .article.clearfix.preview > .lcontrast.m-reset-margin > h2 > a:visited {
                color: #EA9776;
            }

        /* Tagi i data opublikowania: */
            /* Usunięcie obramowania i tła z tagów: */
            #itemsStream > .link.iC > .article.clearfix.preview.dC > .lcontrast.m-reset-margin > .fix-tagline > .tag.create {
                border: none !important;
                background: none !important;
            }
    
            /* Zmiana koloru tagu po najechaniu na niego: */
            #itemsStream > .link.iC > .article.clearfix.preview.dC > .lcontrast.m-reset-margin > .fix-tagline > .tag.create:hover {
                color: #DF6737 !important;
            }
    
            /* Zmiana koloru linka do źródła znaleziska: */
            #itemsStream > .link.iC > .article.clearfix.preview > .lcontrast.m-reset-margin > .fix-tagline > .tag.create > a[rel~="nofollow"] {
                color: #6C808A !important;
            }
    
            /* Zmiana koloru linka do źródła znaleziska, po najechaniu na ten link: */
            #itemsStream > .link.iC > .article.clearfix.preview > .lcontrast.m-reset-margin > .fix-tagline > .tag.create > a[rel~="nofollow"]:hover {
                color: #DF6737 !important;
            }
    
            /* Zmiana koloru linka odpowiedzialnego za zobaczenie innych znalezisk z danej domeny: */
            #itemsStream > .link.iC > .article.clearfix.preview > .lcontrast.m-reset-margin > .fix-tagline > .tag.create > .affect > .fa.fa-list-ul {
                color: #6C808A !important;
            }

            /* Zmiana koloru linka odpowiedzialnego za zobaczenie innych znalezisk z danej domeny, po najechaniu na niego: */
            #itemsStream > .link.iC > .article.clearfix.preview > .lcontrast.m-reset-margin > .fix-tagline > .tag.create > .affect > .fa.fa-list-ul:hover {
                color: #DF6737 !important;
            }

        /* Opisy znalezisk zwykłych i artykułów sponsorowanych oraz kolory linków na stronie głównej: */
            /* Opisy znalezisk zwykłych i artykułów sponsorowanych: */
            #itemsStream > .link.iC > .article.clearfix.preview.dC > .lcontrast.m-reset-margin > .description > .text > a,
            #itemsStream > .link.iC > .article.clearfix.preview > .lcontrast.m-reset-margin > .description.bspace-small > .text > a {
                color: #4C4C4C !important;
            }

            /* Najechanie na opis znaleziska na stronie głównej: */
            #itemsStream > .link.iC > .article.clearfix.preview.dC > .lcontrast.m-reset-margin > .description > .text > a:hover,
            #itemsStream > .link.iC > .article.clearfix.preview > .lcontrast.m-reset-margin > .description.bspace-small > .text > a:hover {
                color: #4C4C4C !important;
            }

            /* Kolory najeżdżanych linków w opisie znaleziska na stronie głównej: */
            /*#itemsStream > .link.iC > .article.clearfix.preview.dC > .lcontrast.m-reset-margin > fix-tagline > .tag.create {
                color: #FF0000 !important;
            }

        /* Przyciski wykopujące, zakopujące i sponsorowane na stronie głównej: */
            /* Obramowanie przycisków "wykop" oraz sponsorowanego na stronie głównej: */
            #itemsStream > .link.iC > .article.clearfix.preview.dC > .diggbox > .ajax > span, 
            #itemsStream > .link.iC > .article.clearfix.preview > .diggbox > a[rel~="nofollow"] > span,
            #dyingLinksBox > .link.dying-links.mark-bg.iC > .article.clearfix.preview > .diggbox > a > span {
                background-image: none !important;
                border-style: solid !important;
                border-width: 1px !important;
                border-color: #E9E9E9 !important;
                border-top-left-radius: 5px !important;
                color: #3C5E6F !important;
            }
            
            /* Przycisk "cofnij" na stronie głównej: */
            #itemsStream > .link.iC > .article.clearfix.preview.dC > .diggbox > .ajax > span.button {
                background-color: #0094DC !important;
                border-color: #1CAFFF !important;
                border-width: 1px !important;
                border-radius: unset !important;
                color: #FAFFFF !important;
            }

            /* Przycisk "wykop" na stronie głównej: */
            #itemsStream > .link.iC > .article.clearfix.preview.dC > .diggbox > .ajax > span.button.submit {
                background-color: #FF6B23 !important;
                border-color: #FF4900 !important;
                border-width: 1px !important;
                border-radius: unset !important;
                color: #FAFFFF !important;
            }

            /* Ukrycie (1) obok tekstu "zakop" na stronie głównej: */
            #itemsStream > .link.iC > .article.clearfix.preview.dC > .diggbox > .dropdown-show > span {
                font-size: 0 !important;
            }

            /* Tekst "zakop" na stronie głównej: */
            #itemsStream > .link.iC > .article.clearfix.preview.dC > .diggbox > .dropdown-show > span:before {
                background-color: #0094DC !important;
                border-color: #1CAFFF !important;
                border-radius: unset !important;
                border-style: solid !important;
                border-width: 1px !important;
                color: #FAFFFF !important;
                content: "zakop" !important;
                font-size: 11px !important;
                left: -49px !important;
                padding-left: 9px !important;
                padding-right: 9px !important;
                position: absolute !important;
                top: 20px !important;
                width: 49px !important;
            }

            /* Powody zakopu na stronie głównej: */
            #itemsStream > .link.iC > .article.clearfix.preview.dC > .diggbox > .dropdown.fix-dropdown.bodyClosable > div {
                border-radius: unset !important;
                border-color: #F3F3F3 !important;
                position: absolute !important;
                top: 26px !important;
                width: 100% !important;
            }

            /* Cofnięcie zakopanego znaleziska na stronie głównej: */
            #itemsStream > .link.iC > .article.clearfix.preview.dC > .diggbox.digout.burried > .ajax > span:nth-child(2) {
                position: absolute !important;
                top: 107% !important;
                left: 0% !important;
                background-color: #0094DC !important;
                border-color: #1CAFFF !important;
                border-width: 1px !important;
                border-radius: unset !important;
                color: #FAFFFF !important;
                padding-left: 8px !important;
                padding-right: 8px !important;
            }

    /* Strona artykułu ze znaleziskiem: */
        /* Przyciski wykopujące i zakopujące na stronie ze znaleziskiem: */
            /* Obramowanie przycisku "wykop" na stronie ze znaleziskiem: */
            #itemsStream > .link.iC > .article.fullview.clearfix.dC > .diggbox > .ajax > span {
                background-image: none !important;
                border: solid 1px #E9E9E9 !important;
                border-top-left-radius: 10px !important;
                color: #3C5E6F !important;
            }

            /* Przycisk "wykop" na stronie ze znaleziskiem: */
            #itemsStream > .link.iC > .article.fullview.clearfix.dC > .diggbox > .ajax > span.button.submit {
                background-color: #FF6B23 !important;
                border-color: #FF4900 !important;
                border-width: 1px !important;
                border-radius: unset !important;
                color: #FAFFFF !important;
            }
}
