@-moz-document domain("vk.com") {
/* Mihey edit
*/
body, .vkpro, .vkmenu, #fc_contacts, 
div.fc_clist_filter_wrap, #fc_clist_filter, #profile_actions a, #im_filter, 
#wk_content, .wk_extpage_head_wrap, .wk_extpage_footer_wrap,
#pv_box,
#settings_panel, .feedback_reply_field, .pv_comment, .mv_controls, .wall_module div.page_media_link_desc,
#profile_main_actions, .tt .tt_text, #reply_submit_hint_opts .radiobtn, .group_list_row, .wall_module .reply{
  color:#999 !important;
  background:#333 !important;
  background-color:#333 !important;
}

.module_header .header_top, .head_nav, #wpe_text, .im_unread_bar_wrap, #pv_comment, #im_peer_controls, .wrh_all:hover, #mv_comment,
#pva_more, #pva_more_photos, #pvsa_more_photos, #albums_load_more, .groups_list_search, #feed_recom_more, #profile_bottom_actions a,.im_editable, #public #subscribe, #public #unsubscribe, #public #unshare,
.wall_fixed .wall_fixed_more, .wall_fixed .wall_module .reply_box,#ts_cont_wrap,
.profile_info_link:hover, #profile_groups_link:hover, .tld_d, #ac, #profile_gifts, #profile_friends, #profile_idols, #profile_albums, #profile_videos, #profile_audios{
  background:#2A2A2A !important;
  background-color:#2A2A2A !important;
  color:#a8a8a8 !important;
} 
#wrap2, #header_wrap1, .im_log_t td {
  border: none !important;
}
#wk_box {
  background: black!important;
  background-color:0 !important;
}
#wrap1 {
  border-width: 0px 0px 1px 1px;
}
div.ts, #header, #im_resizer_wrap, .wk_text blockquote, .profile_top_sep, .dialogs_new_msg,
.im_new_msg .im_log_author, .im_new_msg .im_log_body, .im_new_msg .im_log_date, .replies_side_over {
  background: #2A2A2A !important;
  background-color: #2A2A2A !important;
}
.module .close {  
  background: #222 !important;
  background-color: #222 !important;
  border-top: solid 0px #CCC !important;
  cursor: pointer;
}
.gifts_list_module a span.thumb, .counts_module div a span.thumb {
  background: none;
}
.wall_post_source_iphone, .wall_post_source_ipad, .wall_module .post_like .no_likes, .wall_module .post_like .my_like, .wide_wall_module .like_wrap {
  visibility: hidden !important;
} 
  
.page_media_poll .page_poll_row_count, .dialogs_row_over, .fc_msgs_unread {
    background: #555 !important;
    background-color: #555 !important;
}
.post_media .audio.over .actions, .module .close{
    background: #444 !important;
    background-color: #444 !important;
}
/*darker all page*/
*, .vkpro, .vkmenu {
  border-color:#2A2A2A !important;
}
.im_log_t td {
  border: none;
}
.module_header, .vkmenu ol li a, #im_footer_filler, #im_footer_sh, a.wk_ext_link {
  background:#333 !important;
    background-color:#333 !important;
}  
  
.vkmenu {
    background: #333 !important;
  background-color: #333 !important;
}  

.feedback_row_clickable_over .feedback_row_answer_wrap {
	border: 1px solid #444;
	background: #555;
    background-color: #555;
}

#feed_news_bar, #feed_bar, #im_bar, #group_like_module, .group_block_module, #im_sound_controls,
.feedback_reply_row_wrap, #feedback_unread_bar{
	background:#444 !important;
    background-color:#444 !important;
} 
.wk_external_content .wk_header, .wk_external_content .wk_sub_header, .wk_external_content .wk_sub_sub_header, .pv_reply_greeting, a.wk_extpage_head_domain{
  color: #bbb !important;
}
.reply_field_tail {
  background: none;
}

/* 
    VK Xyon Darker
    Author: Pavlo Tymoshenko;
    Page: http://vk.com/pavlo_tumosh https://plus.google.com/u/0/112389814097942477541/posts ;
    License type: CC BY-NC-SA;
    Date: 18 July 2014;
    This is a minimalized code
*/
div.feed_article_desc {color:#999 !important;}
    a, a.wk_extpage_head_domain {color:#78A0B9}
    .button_blue button, .button_blue.button_disabled button:active, .button_blue.button_disabled button.active, .button_blue.button_disabled button:hover, .button_blue.button_disabled button.hover {background:#666;color:#a8a8a8;text-shadow:none}
    .button_blue button:active {background:#777}
    .photos_album_page .summary_wrap, .photos_albums_page .summary_wrap, .photos_comments .summary_wrap, .photos_tag_page .summary_wrap, #photos_edit .summary_wrap, #photos_albums .summary_wrap {padding:20px;margin:0}
    .input_back  {padding:5px 5px 6px !important}
    #ts_input {background:#333;border:0;color:#999}
/*auth*/
    .login_mobile_header,.login_loggedout_header,#quick_login .label,.login_m_vk_com a,.login_mobile_name,.login_mobile_menu li,h2,#quick_login .text,#jpv_desc,#jpv_summary .summary,.lang-column a, .lang-column b {color:#a8a8a8}
    .login_m_vk_com,.login_mobile_cont,#quick_login .text,#jpv_box,.bnav_a:hover, #feed_bar {background:#444}
    #ijp_input, #ij_first_name, #ij_last_name, #ij_full_name {background:#444;color:#a8a8a8}
    .login_mobile_new {background:#555}
    .login_mobile_head {background:#000}
/*attention*/
    .left_box {background:#333}
    .left_box h4 {color:#a8a8a8 !important}
/*profile*/
    h4.page_top,.my_current_info {color:#999;background:#333} /*top*/
    .wall_module #submit_post_box,.p_header_bottom {background:#444;color:#999} /*bottom header*/
    #profile_actions a {background:#444;color:#999} /*profile menu*/
    #profile_actions a:hover, #profile_bottom_actions a:hover,.counts_module div a:hover,.profile_info_link:hover, #profile_groups_link:hover {background:#666;color:#a8a8a8} /*menu hover*/
    #profile_full_info h4 b,#profile_full_info h4 a.fl_r, #profile_full_info h4 a.fl_l {background:#333;color:#a8a8a8}
    #profile_main_actions {background:#333}
    .feedback_row_clickable_over2 {background:#444}
    a.wall_reply_greeting {color:#999}
    .feedback_row_answer_wrap {background:#444}
    .feedback_row_clickable_over2 .feedback_row_answer_wrap {background:#555;color:#a8a8a8}
    .feedback_answer_tail {display:none}
/*wall*/
    .wall_module #submit_post_box textarea {background:#333}
    .post_like_link, .post_share_link {color:#999 !important}
    .wall_module .post_like:hover, .wall_module .post_share:hover {background:#444}
    .wall_module #page_wall_posts.all .all {background:#333}
    .wide_wall_module {background:#333}
    .audio.over .area {background:#444}
    .wall_reasons_list {color:#999}
    .wr_header {background:#333;color:#999}
    .wall_module .reply_info .like_link {color:#999}
/*sidebar*/
    #fmenu {background:#333}
    #side_bar,#side_bar ol li .left_row {background:transparent}
    #side_bar ol li .left_row:hover {background:#444}
    #side_bar a {color:#999}
    a:hover .left_count_wrap,a:hover .left_count_persist .left_void, .left_nav_over .left_count_pad .left_void,.left_count_wrap {background:#666;color:#a8a8a8}
    .stl_active.over_fast #stl_bg {background:#444}
    .stl_active.over_fast #stl_bg nobr,.stl_active #stl_bg nobr {color:#999;background:none;padding:0}
/*chat*/
    .chat_active, .chat_expand, .chat_onl_wrap:hover, .chat_tab_wrap:hover {background:#444}
    .chat_onl_icon {background-position:-52px -45px}
    .fc_out_msg_arr {display:none}
    .chat_onl {color:#999}
    .fc_tab_log_wrap {background:#444}
    .fc_tab_head {background:#666}
    .fc_tab_title {color:#a8a8a8}
    div.fc_tab_txt,.fc_msgs_more {background:#666;color:#a8a8a8}
    .fc_msgs_out .fc_msgs {background:#666;color:#a8a8a8}
    .fc_msgs {background:#333}
    .fc_msgs_out .fc_msgs_date {color:#adadad !important}
    .fc_scrollbar_cont {background:#666}
    .fc_scrollbar_inner  {background:#a8a8a8}
/*friends*/
    #friends .tabs {background:#333}
    ul.t0 li.active_link a,ul.t0 li.active_link a:hover {background:#444;color:#a8a8a8}
    ul.t0 li.active_link a b.tl2, ul.t0 li.active_link a:hover b.tl2,ul.t0 li.active_link a b.tl1 b, ul.t0 li.active_link a:hover b.tl1 b {background:#444 !important;border-color:#444 !important}
    ul.t0 a {background:#333;color:#999}
    ul.t0 a .tl1,ul.t0 a .tl1:hover {display:none}
    ul.t0 a .tl2,ul.t0 a .tl2:hover {visibility:hidden;border:0}
    #friends .user_block .actions {background:#333}
    #friends .user_block .actions a {color:#999}
    #friends .user_block .actions a:hover {background:#444}
    ul.t0 li a:hover {background:#444;color:#999}
    #friends .search {background:#444}
    #friends #s_search {background: #333;color:#999}
    #friends .search .input_back {padding: 5px 40px 6px 10px !important}
    .summary_wrap {background:#444;color:#a8a8a8}
    #friends .side_panel {background:#444;color:#999}
    .friends_fltr_label {color:inherit}
    .selector_container td.selector {background:#333}
    .result_list ul li {background:#555;color:#999}
    .result_list ul li.active {background:#666}
    .selector_container td.selector_dropdown {background:url('http://vk.com/images/darr_dd_out.gif') no-repeat center center / 7px 4px #666}
    .selector_focused.selector_container td.selector_dropdown,.selector_dropdown_hover {background:url('http://vk.com/images/darr_dd_out.gif') no-repeat center center / 7px 4px #999 !important}
    #friends .sections_block .cur_section {background:#666 !important;color:#a8a8a8}
    #friends .sections_block .side_filter {background:#333;color:#999}
    #friends .sections_block .side_filter:hover {background:#555}
/*photos*/
    ul.t0 .t_r a:hover {background:#333}
    .box_body,#pv_box {background:#333;color:#999}
    .box_controls {background:#444 !important;color:#999}
    #box_layer .photos_privacy_control {border:0}
    .photos_edit_header,#pv_comment_header,#pv_like_wrap {color:#a8a8a8}
    #box_layer .photos_edit_title, #box_layer .photos_edit_description,#pv_comment {background:#444;color:#999}
    .box_title_wrap,.box_dark .box_title  {background:#444}
    .box_dark .box_x_button {color:#999}
/*media*/
    .add_media_head,.add_media_items {color:#999 !important;background:#444 !important}
    .add_media_item {background-color:#333;color:#999}
    .add_media_item:hover {background-color:#444;color:#a8a8a8}
/*video*/
    .video_search {background:#444}
    .video_search #v_search {background:#333;color:#999}
    .video_main_summary {margin:0;padding:15px}
    .video_length {color:#999}
    .video_length_sel {background:#666}
    .video_album_edit_header {color:#a8a8a8}
    #video_album_edit_title {background:#666;color:#a8a8a8}
/*audio*/
    #audio.new .audio_search {background:#444}
    .audio_search .s_search {background:#333}
    #ac {background:#666}
    #ac .performer {color:#a8a8a8}
    #ac .title,#ac .duration {color:#999}
    #ac .ctrl {background-color:#fff;padding:1px}
    #side_panel {background:#666;color:#999}
    .audio_filter.selected {background:#777}
    .audio_filter {color:#999}
    .audio_filter.over {background:#555;color:#999}
    .audio_friends_search_btn {background:#333 !important;color:#999}
    .audio_friend {background:#666}
    .audio_friend_status {color:#a8a8a8}
    .audio_friend_name_now,.audio_friend_name {color:#ccc}
    .audio_more_friends {background:#555;color:#999}
    .audio_more_friends:hover {background:#777;color:#999}
    .ac_back_line {background:#999}
    .ac_load_line {background:#888}
    .ac_progress_line {background:#ccc}
    .ac_slider {background:#ccc}
    #audio.new .audio.over .area,#audio.new .audio.tt_shown .area {background-color:#444}
    #audio.new .audio.current .area,#audio.new .audio.current.tt_shown .area #audio.new .audio.current.over .area {background-color:#888}
    #gp_wrap {background:#444}
    #gp_performer, #gp_title {color:#a8a8a8}
/*message*/
    .tabs.t_bar {background:#333}
    #im_bar,.im_tab_over .im_tab2 {background:#444}
    #im_filter,span.post_video_duration {background:#333;color:#999}
    .dialogs_row_over {background:#555}
    .dialogs_msg_text {color:#999;width:calc(100% - 50px)}
    #im_sound_controls {background:#444}
    .im_friend_over {background:#555}
    .im_friend,.im_tab3,.page_list_module .people_desc,.wall_module .media_desc .a {color:#a8a8a8}
    .im_find_in_mail.im_friend_over,.im_friend_not_found,.replies_open {background:#444;color:#999}
    #im_peer_controls_wrap  {background:#444}
    .im_editable,.im_tab_selected .im_tab2 {background:#333;color:#999}
    #im_peer_controls {width:-moz-max-content;width:-webkit-max-content;width:-o-max-content;width:-ms-max-content;margin:auto}
    .dialogs_new_msg,.feedback_row_clickable_over1, .feedback_row_clickable_over2, .feedback_row_clickable_over3, .feedback_row_clickable_over4  {background:#444}
/*group(s)*/
    .groups_list_search,#search_query_wrap,#public .like_module,#public #subscribe, #public #unsubscribe, #public #unshar,.wall_fixed .wall_fixed_more,.wall_module .reply_fakebox_wrap,.wall_module .reply_box {background:#444;color:#999}
    #groups_list_search,.wall_fixed .wall_module .post,.pv_comment,.reply_field_wrap {background:#333}
    .group_list_row,.public_info {background:#333}
    .group_row_labeled a,.search_subtab1,.current_text {color:#999}
    .search_subtab1:hover,.wall_fixed .wall_fixed_more:hover {background:#555}
    .search_subtab1.active {background:#666}
    #search_query,#public .wall_module,.wall_module .reply,.wall_module .reply_fakebox {background:#333;color:#999}
    .search_table td.results,.search_results td.results {background:#333}
    .wall_tt .wrap, .bp_tt_wrap, .mott_wrap, .verified_tt_wrap, .gifts_tt_wrap {background:#444;border-color:#555}
    .search_table td.filters {background:#444}
    .search_catalog_row.selected, .search_catalog_row.selected:hover {background-color:#666;color:#fff}
    .search_catalog_row:hover {background-color:#555;color:#a8a8a8}
    .search_catalog_row {color:#999}
    #pv_summary .summary {color:#a8a8a8}
/*news*/
    #feed_bar {background:#444}
    .summary_tab_sel .summary_tab2, .summary_tab_sel .summary_tab2:hover {background:#666}
    .summary_tab2:hover,.olist_item_wrap_over {background:#555}
    .summary_tab3 {color:#a8a8a8}
    #feed_search {background:#333}
    #feed_searchext_exclude, #feed_searchext_link, #feed_searchext_content,.olist_empty {background:#333}
    .feed_serachext_header,.olist_item_name,.olist_empty {color:#a8a8a8}
    input.olist_filter {background:#333}
    .box_dark .tabbed_box .summary_tabs,.olist_filter_wrap {background:#444}
    .olist_item_wrap_over .olist_item_over_label,a.olist_controls_ignored {color:#888}
/*pins*/
    .summary_tabs {background:#444}
    #fave_new_link {background:#333}
    .fave_link_des {color:#999 !important}
/*settings*/
    #settings_panel,[class^="settings"],#settings_bl_search,#new_ticket,#profile_editor {background:#333}
    .settings_privacy_control {border-color:transparent !important}
    .settings_section h2, .settings_privacy_header, .setbal_section h2 {color:#ccc}
    #settings_old_pwd, #settings_new_pwd, #settings_confirm_pwd, #settings_new_mail, #settings_new_phone,#settings_addr,.settings_edit_addr_label {background:#444;color:#999}
    .settings_view_as_text,#pedit_first_name, #pedit_last_name, #pedit_middle_name, #pedit_maiden_name, #pedit_home_town,#pedit_mobile, #pedit_home, #pedit_skype, #pedit_website,#pedit_interests_activities, #pedit_interests_interests, #pedit_interests_music, #pedit_interests_movies, #pedit_interests_tv, #pedit_interests_books, #pedit_interests_games, #pedit_interests_quotes, #pedit_interests_about,.pedit_edu_txt {background:#444;color:#999}
    #settings_panel .msg,#settings_bl_bar {background:#444}
    .msg, .box_msg {background:#403C25}
    h4,.tickets_dmca_head, .tickets_pay_head {color:#a8a8a8}
/*catal darkerizer*/
    .apps_feed_info_str,.tdd_input,.docs_empty,info_tab,.page_actions a,.tickets_faq_row.over.detailed,.tickets_faq_row.over,#tickets_name,#tickets_post_field_wrap,#tickets_post_field {background:#333;color:#999}
    .apps_block_more,.app_tabs.t_bar,#apps_search_wrap,.app_search,.docs_search_bar {background:#444}
    .app_search .s_search, .apps_search_wrap .s_search, .app_search .s_search_smal,#tickets_reply {background:#333;color:#a8a8a8}
    #apps_search_wrap a.active, #apps_search_wrap a.active:hover,.docs_add_new,.dev_section_menu:hover, .dev_section_submenu:hover {background:#555;color:#999}
    #apps_search_wrap a,.tickets_faq_title,#tickets_name .info,ul.listing li span {color:#999}
    #apps_search_wrap a:hover,.apps_genres .add_media_head {background:#555;color:#999}
    .tdd_search .tdd_control,.docs_side_panel,.page_actions a:hover,.dev_main_head,.dev_header,.dev_sidebar,.wk_text blockquote,.jobs_head {background:#444;color:#999}
    .docs_add_new:hover,.dev_sect {background:#888;color:#ccc}
    .side_filter.cur_section,.dev_mlist_sel {background:#777;color:#ccc}
    .side_filter,#page_wall_posts,.community_no_head .community_groups_main,input.dev_section_search {background:#333;color:#999}
    #terms_box_content .wikiSubHeader, #privacy_box_content .wikiSubHeader,.help_privacy_header,.dev_main_header,.color2,.dev_page_cont .wk_header,.jobs_head_title,.jobs_job .jobs_job_title {color:#ccc}
    .dev_section_menu, .dev_section_submenu,.jobs_job .jobs_job_about {color:#999}
    .jobs_job:hover {background:#333}
    .wk_text blockquote {border-left-color:#888}
}
