css:
@-moz-document domain("youtube.com") {
/* Hide scrollbar */
::-webkit-scrollbar { 
  display: none !important;
  width: 0px !important;
}

/* Hide annotations */
#player .html5-video-content,
#player .html5-video-content,
#alerts,
.html5-video-content .video-annotations,
.annotation {
  display: none;
  visibility: collapse;
}

/* Black background */
#yt-masthead-container {
  background: black !important;
  border-bottom: 1px solid #333 !important;
}

/* Full width */
#player {
  margin: 0 !important;
  width: 100% !important;
  top: 0 !important;
  max-width: 9999px !important;
}

#player .html5-video-player,
#player .html5-progress-bar {
  width: 100% !important;
}

.ytp-chrome-bottom {
   width: 100% !important;
   left: 0 !important;
}

.ytp-progress-bar-container {
   bottom: 28px !important;
}

/* Full height: height of your desktop - 135 */
#player #player-api {
  margin: 0 !important;
  width: 100% !important;
  height: 945px !important;
  left: 0 !important;
  position: relative !important;
}

/* Full height: height of your desktop - 135 - 30 */
#player video {
  width: 100% !important;
  height: 915px !important;
  left: auto !important;
  top: 0px !important;
}

#placeholder-player * {
  background: #fff;
}
}
