css:
@-moz-document url-prefix("http://www.bf-blog.cz/"){
html, body, .recent-comments .info, .meta-desc {
  	font-family: 'Helvetica Neue', Helvetica, Arial !important;
	font-size: 13px !important;
}

.primary-nav nav a, nav > ul > li:after, #az-listing a, .tooltip-content, .tooltip-content2, #navigation ul ul a, #navigation ul ul a:link, #navigation ul ul a:visited {
    font-family: 'Helvetica Neue', Helvetica, Arial !important;
	font-size: 14px !important;
}
 
#logo a img {
  width: 50% !important;
  margin: 2% 0 0 10% !important;
}

.primary-nav nav {
  padding-top: 15px !important;
  padding-bottom: 0 !important;
}

#navigation .menu {
  float: left !important;
  max-width: 100% !important;
}
   
.article {
  width: 70% !important;
  float: left !important;
  margin-bottom: 10px !important;
  padding: 10px !important;
}

/* 29.10.2014 - Update */
.post.excerpt .featured-thumbnail img {
  width: 150px !important;
  height: 80px !important;
  max-height: 80px !important;
  min-height: 80px !important;
  max-width: 150px !important;
  float:right !important;
  overflow: hidden; 
}

.featured-thumbnail {
  position: relative !important;
  float: right !important;
  clear:both !important;
  width: 20% !important;
}
.home-content {
  width: 66% !important;
  padding: 0 !important;
  position: relative !important;
  float: left !important;
}
.home-cat {
  position: absolute !important;
  top: 0px !important;
  padding: 1px 1px !important;
  background: #dd901c !important;
}
#navigation {
  margin: 2px !important;
  float: left;
  font-size: 13px !important;
  width: 1200px !important;
  z-index: 1000 !important;
}
#navigation ul ul a:hover {
  color: #dd901c !important;
}
.related-posts {
  margin-top: 15px !important;
  float: left !important;
  width: 0 !important;
  display: none !important;
}
.post-share {
	margin: 0px 0px 0 0 !important;
}
.nav-center {
  border-color: #282929 !important;
}
.login-button a {
  padding: 2px 5px !important;
}
.home-cat .meta-desc {
  font-weight: normal !important;
}
}
