@-moz-document domain(bazos.sk) {



html{
    box-sizing: border-box;
}
*, *:before, *:after{
    box-sizing: inherit;
    margin: 0;
    padding: 0;
}


.strankovani {
  word-spacing: 0px;
  }

a:hover{
 text-decoration:none; 
}

