css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("weibo.com") {
  
  #v6_pl_rightmod_recominfo,
  #v6_pl_content_publishertop .title_area,
  #v6_pl_content_homefeed .WB_main_r,
  .WB_main_r > div
  {
    display: none;
  }
  
  .FRAME_main .WB_frame
  {
    width: 760px !important;
  }
  
  .FRAME_main #plc_main
  {
    width: 610px!important;
  }
  
  #v6_pl_content_publishertop .input
  {
    margin: 0 !important;
  }
  
  #v6_pl_content_publishertop .send_weibo
  {
    height: 121px !important;
  }
  
  .WB_feed_detail
  {
    padding: 18px !important
  }
  
  .W_face_radius
  {
    border-radius: 10% !important
  }
  
  .WB_face,
  .WB_face .face img
  {
    width: 30px !important;
    height: auto;
  }
  
  .WB_detail
  {
    margin-left: 0 !important;
  }
  
  .WB_detail > .WB_info {
    height: 26px;
    margin-left: 40px;
  }
  
  .WB_row_line li
  {
    height: 30px !important;
  }
  
  .WB_row_line .line
  {
    margin: 3px 0 !important;
  }
  
  .WB_sonFeed .WB_feed_detail
  {
    margin: -1px 0 0 0 !important;
  }
  
  .WB_sonFeed .WB_text
  {
    margin-left: 10px !important;
  }
  
  .WB_sonFeed .WB_func
  {
    margin-left: 40px !important;
  }

}
