css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("elearn.rodp.org") {
/*
TODO:
Replace ROCC logo
Utilize vui-* classes
choose prettier defaults
menufloats :hov
*/
:root { /* Variables var(--) */
   --main-bg:     #000000;
   --main-fg:     #00ffea;
   
   --title-bg:    #1f1f1f;
   --title-fg:    #00ffea;

   --link:        #00ffea;
   --link-hov:    red;

   --button-bg:   #7f7f7f;
   --button-fg:   #00ffea;
   --main-button-hov:   #00ffea;
   /* Calender */
   --cal-wday-bg:       #1f1f1f;
   --cal-wday-fg:       #00ffea;
   --cal-wday-event-bg: var(--cal-wday-bg);
   --cal-wday-event-fg: #900000;
   /* Calender Weekends*/
   --cal-wend-bg:       #1f1f1f;
   --cal-wend-fg:       #00ffea;
   --cal-wend-event-bg: var(--cal-wend-bg);
   --cal-wend-event-fg: #900000;
}
/* Main BG */
body,.d2l-background-left,.d2l-background-right,.d2l-page-main-padding {
   background-color: var(--main-bg);
   color: var(--main-bg);
}

.d2l-page-main-padding {
   background-color: var(--main-bg);
   color: #00ffea;
}
.d2l-minibar-placeholder { /* Top Floating Blue Menu Bar */
}
/*widgets*/
.d2l-widget-header {
   background-color: var(--title-bg);
   color: var(--title-fg);
}
.d2l-widget-content {
   background-color: var(--main-bg);
   color: var(--main-fg);
}
a {
 color: var(--link) !important;	
}
/* Mail */
.dbd_frame {
   background-color: var(--main-bg);
   color: var(--main-fg);
}
.d2l_form .dtn * * {
   background-color: var(--main-bg);
   color: var(--main-fg) !important;
}
.dtc * {
   background-color: var(--main-bg);
   color: var(--main-fg) !important;
}
/* Menu Floats*/
.d2l-menuflyout-contents {
   background-color: var(--main-bg);
   color: var(--main-fg);
}
/* Title Boxed */
.d2l-datalist-item-content, .d2l-datalist-item-content * {
   background-color: var(--title-bg);
   color: var(--title-fg);
}
.d2l-heading {
   /*background-color: var(--title-bg);*/
   color: var(--title-fg);
}
.d2l-page-header {
   background-color: var(--title-bg);
   color: var(--title-fg);
}
.d2l-page-header-dark {
   background-color: var(--title-bg);
   color: var(--title-fg);
}
.d2l-collapsepane-header { /**/
   background-color: var(--button-bg);
   color: var(--title-fg);
}
/* twopannel pages, like course content */
.d2l-twopanelselector-wrapper .d2l-twopanelselector-padding {
   background-color: var(--title-bg); 
}
.d2l-le-TreeAccordionItem-anchor,.d2l-le-TreeAccordionItem-anchor * {
   background-color: var(--button-bg);
}
/* calender/task columb*/
.d2l-column-flip-side {
   background-color: #1f1f1f;
   color: #00ffea;
}
/* Things like Discussion List. this may be overzelous */
thead * {
   background-color: var(--title-bg);
   color: var(--title-fg);
}
tbody * {
   background-color: var(--main-bg);
   color: var(--main-fg);
}
/* BEGIN CALENDER */
.d2l-calendar-mini { /* Weekdays (sets calender defaults) */
   background-color: var(--cal-wday-bg);
   color: var(--cal-wday-fg);
}
.d2l-calendar-mini-event { /* WeekDays with events */
   background-color: var(--cal-wday-event-bg);
   color: var(--cal-wday-event-fg);
}
.d2l-calendar-mini-weekend { /* WeekEnd Headers */
   background-color: var(--cal-wend-bg);
   color: var(--cal-wend-fg);
}
.d2l-calendar-mini-day.d2l-calendar-mini-weekend { /* WeekEnds */
   background-color: var(--cal-wend-bg);
   color: var(--cal-wend-fg);
}
.d2l-calendar-mini-weekend.d2l-calendar-mini-event { /* WeekEnds with events */
   background-color: var(--cal-wend-event-bg);
   color:            var(--cal-wend-event-fg);
}
/* END CALENDER */

/* NavBar */
.d2l-navbar-item * {
 color: var(--link) !important;	
}
.d2l-navbar-item *:hover {
 color: var(--link-hov) !important;	
}
/* Most "links" on d2l (clickable things that don't redirect you surprisingly dont fall under this) */
.d2l-clickable * {
 color: var(--link) !important;
}
.d2l-clickable:hover * {
   color: var(--link-hov) !important;
}
.vui-button { /* Buttons, only affects pressed ones for now. */
   background-color:    var(--button-bg) !important;
   color:               var(--button-fg);
}
}
