css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('eow.alc.co.jp') {
    /*
    Eijiro on the Web - Simple
    author: lingmoon
    version: 3.5
    update: 2016-03-09
    license: CC0
    */

    body {
        font-family: sans-serif;
    }
    a {
        color: #666 !important;
    }
    #AreaUpperLeftInner > p,
    #AreaHeaderRight,
    #AreaUpperRight,
    #searchHeader > dl,
    #eijiro_img,
    #pagetop,
    #introduceEowp,
    #AreaFooter,
    #paging span:first-child,
    #paging span:last-child,
    #ShopButton,
    #searchHeader,
    .navif #itemsNumber,
    .cl_l,
    .bg_i,
    .setting,
    .pr_text,
    .smallredfont,
    .tango,
    .eijiro_tit,
    .adArea,
    .sns,
    .mb_0 {
        display: none;
    }
    #logo {
        overflow: hidden;
    }
    #HomeButton {
        padding-top: 30px !important;
    }
    #AreaBody {
        max-width: 980px;
        margin: 0 auto;
    }
    #AreaUpperLeft #search_area,
    .search_area_left,
    .search_box,
    #AreaUpperLeftContainer,
    #navigation,
    #paging {
        width: 100% !important;
        max-width: none;
    }
    #AreaUpperLeftContainer {
        margin-right: 0;
    }
    .search_box {
        box-sizing: border-box;
        height: 4em;
        margin: 0 !important;
        padding: .5em 1em;
        border-radius: 10px 10px 0 0;
    }
    .search_window {
        width: 85%;
    }
    .search_window .s_txt1 {
        transition: all .7s ease;
        border: 1px solid #a3a3a3;
        border-radius: 5px;
        background-image: linear-gradient(#f1f1f1, #fff);
    }
    .search_window .s_txt1:focus {
        border: 1px solid #fad531;
        background-image: linear-gradient(#f5eb8e, #fff);
    }
    #sw {
        border-top: none;
    }
    .redtext {
        font-weight: bold;
        color: #333;
    }
    .midashi {
        font-weight: normal;
        margin-bottom: .5em;
        padding-left: 0;
        color: #555;
    }
    #resultsList ul > li {
        margin-bottom: 1em;
        padding-bottom: 1em;
        border-bottom: 1px dotted #ccc;
    }
    #resultsList li div {
        font-size: .875rem;
        line-height: 1.7;
        padding-left: 1em;
        border-left: 3px solid #e0e0e0;
    }
    #resultsArea strong a:link {
        border-bottom: 1px dashed #333;
    }
    #sw {
        margin-bottom: 2em;
        border-radius: 0 0 10px 10px;
    }
    #searchWord {
        color: #333;
    }
    #AreaHeaderLeft {
        float: none;
        margin: 0 auto;
    }
    .wordclass,
    .hklabel {
        color: #333;
    }
    span.ls_normal {
        color: #333;
        border-color: #999;
        border-radius: 3px;
        background-color: #f1f1f1;
    }
    #resultsArea .navif {
        border: none;
    }
    .navif #paging {
        padding-top: 3em;
    }
    .search_area_left {
        overflow: hidden;
        width: 100%;
        max-width: 100% !important;
    }
    .s_txt1 {
        font-size: 26px;
        padding: .3em;
    }
    .search_box {
        height: auto;
        background: #ccc;
    }
    #fixedBox {
        height: auto !important;
        ;
    }
    #AreaBody > div {
        height: auto !important;
    }
    #sw {
        margin-bottom: 0 !important;
    }
    #navigation {
        float: none;
        width: 99% !important;
        padding: 0 0 1% 1%;
        border-radius: 0 0 10px 10px;
        background: #f1f1f1;
        ;
    }
    #itemsNumber{
        background: #f1f1f1;
    }
}
