css:
@-moz-document url-prefix("https://www.skydesk.jp/ja/products/crm/") {
}
@-moz-document url-prefix("https://crm.skydesk.jp") {
.hPad {	padding: 3px 0px 1px;}
	.lbox {	margin: 2px 0 2px 0;}
	.p15 {padding: 5px !important;}
	.newPopupTitle{	padding: 2px 0 2px 0 !important;}
	div.individualSetup{	margin: 0 3px 15px 3px !important;}
	.rwobwop{	padding-bottom:50px !important;}
	.setupTable .setuplink{	font-size: 200% !important;}
	#personal{height:4em;}
	.menu a{font-size:14px;	font-weight:bold;letter-spacing:-1px;}
	strong{	font-size:14px;	font-weight:bold;}
	.individualSetup{height:140px !important;}
	.dbtn, .button, .btn{font-size:14px;}
	.smalledit, #header_Email .smalledit, #crmspanid a, .dvValueTxt .smalledit, .date , .ddM li,.ddM a{
	font-size:13px !important;}
	.dvgrayTxt,.dvValueTxt, .dvValueTxt a{padding: 2px 2px 0 0; }
	.dvValueTxt, .dvValueTxt a{font-size:14px;}
	#fullInfoDiv .label,#fullInfoDiv .value{padding:1px 2px !important;}
	#fullInfoDiv .value{font-size:14px;}
	.setupTable a.setuplink{font-size:14px !important;color:#222;}
	li.aTul a#tab_Forecasts,a#tab_Products,a#tab_Emails,a#tab_Documents,a#tab_Forecasts,a#tab_Dashboards,a#tab_PriceBooks,ul.addTabsul>li.aTul>a.menuOn{font-size:14px !important;}
	table.menuTable a{font-size:14px;}
	ul.setupleftul a{padding: 5px 20px;font-size:16px;}
	#help #contentcontainer p {padding: 0;}
	#lvTred td {padding-top:5px;padding-bottom:2px;}
	#idForCV {padding-top:2px;}
	.idForCVpadding{padding: 2px 0px 0px;}
	.cvpadding{padding-bottom:2px;}
	.topdivbg1 .menu a{padding: 3px 5px 4px;letter-spacing:-1px;}
	.newPopupTitle, .newpopuptitle, .blankStateTitle{font-family: Verdana, "游ゴシック", YuGothic, "Hiragino Kaku Gothic ProN", Meiryo, sans-serif;}
}

@-moz-document url-prefix("https://portal.skydesk.jp") {
body{margin:3px;color:#111;}
	.bootstrap #statusLink.selected, .bootstrap #announcementLink.selected{color:#444;font-weight:bold;font-size:120%;}
	.bootstrap textarea.announcement{border: 2px solid #FFAD57;}
	.bootstrap .ui-widget-content{border: 2px solid #ccc ;}
	.darkBlue{color:#02a !important;}
	.feedInputWrapper{padding-right:8px;}
	span.hint{color:#111;font-size:88%;}
	span.peopleFullName{font-size:16px;}
	table tr td div p a {color:#02a !important;}
	.feedRow .feedTime{color:#02a;}
	.announcementRow{;background-color:#F5E7C4;}
	.bootstrap #feed .tileContent,.bootstrap .feedRow{background-color:#fff;}
	.feedRow .sender{color:#02a;}
	a, .pseudoLink{color:#249;}
	.fluid{	margin: 0 303px 0 303px}
	.tileContent{padding: 3px 4px}
      pre.feedMessage{line-height:1.25em;letter-spacing:-1px;margin: 1px 2px;}
	pre.feedMessage a{line-height:1.25em;letter-spacing:-1px;}
	.announcementRow,.feedRow{padding:2px 5px 4px;}
	.announcementRow .feedTime,.feedRow .feedTime{font-size:85%;line-height:1em;text-align:right;margin-left:0.5em;}
	#postFeedSwitcher,#postFeed{padding:0;}
	#feedPostAction{margin-bottom:13px;}
	.hint{font-size:12px;}
	.updatesTargetMenu,#postFeedButton{padding-top:0;margin-top:0;}
	.updatesTargetMenu .updatesTargetMenuItems{color:#02a;}
	#postFeedButton,#postToButton{padding-bottom:0;border: 1px solid #888;padding:1px;}
	#feedList{position:relative; top:-3px;}
	.bootstrap #postFeed {border-bottom: 1px solid #888;}
	#postFeedButton,#postToLabel,.updatesTargetMenu{font-size:14px;font-weight:bold;}
	#feedInput{margin-top:0;position:relative; top:-3px;}
	pre.feedMessage{overflow-x:visible;}
	.navbar .nav>.active>a, .navbar .nav>.active>a:hover, .navbar .nav>.active>a:focus,.navbar .nav>li>a {color:#111}
}

@-moz-document regexp(".*skydesk.jp.*") {
body,td,a,.feedItem,span,div, .h1, .h2, .title, .h3,.entry-header .entry-title,#content .page .entry-title,.crm-moto,select,input,textarea{
		font-family: Verdana, "游ゴシック", YuGothic, "Hiragino Kaku Gothic ProN", Meiryo, sans-serif;
}
.singlepost .entry-title,.page-title, .widget-title{
		font-family: Verdana, "游ゴシック", YuGothic, "Hiragino Kaku Gothic ProN", Meiryo, sans-serif !important;font-weight:bold !important;
}
}

@-moz-document url-prefix("https://docs.skydesk.jp") {
.docs-topbutton li span a{font-size:14px;}
}

@-moz-document url-prefix("https://www.skydesk.jp/ja/apps/crm/") {
h1, div.h1{padding-top:10px;}
	#bw {min-height:30px;}
	#help .s1Nav li{line-height:10px;}
	#help .bc { padding: 3px 0;}
	#help h1 {margin-bottom:3px;}
	#help p, #help .s2 ul li, #help .s2 ol li, #help .tdMain, #help .tdmain, #help .tdSub, #help .tdsub, .top a{line-height:1.4em;}
	td.tdsub{padding:1px 3px !important;}
	.IP table { margin: 0 !important;}
	li{padding-bottom:0px !important;}
	.IP ul, .IP ol, .nc .s2 ul, .nc .s2 ol{margin: 0 0 2px 35px;}
	.IP h1, .IP h2, .IP h3, .IP h4, .IP h5, .IP h6{padding-top:3px;padding-bottom: 3px;margin-top:3px ;margin-bottom:3px;}
	div.script{margin-top:0;margin-bottom:3px;padding:3px 10px;}
	#help h1,#help h2,#help h3,#help h4{margin:3px;}
	#secondary ul li a{padding:3px 0 3px 10px;}
}

@-moz-document url-prefix("https://creator.skydesk.jp/") {
	*.zc-formbutton{font-size:14px;}
	.zc-labelheader,.zc-inputheader{padding: 5px 10px;}
	#zc-viewcontainer_view table thead tr th.zc-viewrowheader {border-right:1px !important;}
	*.zc-viewrow td{border-right:1px solid #eee;padding:1px 2px 4px 2px;}
	*.zc-edit{padding:1px 2px 4px 2px !important;}
	*.zc-delete{padding:1px 3px !important;}
}

@-moz-document url-prefix("https://task.skydesk.jp") {
	body, table, a, div, em, input, span, select, textarea, ul, li, .bodytxt{font-size:14px !important;}
	input,li,textarea{font-family: Verdana, "游ゴシック", YuGothic, "Hiragino Kaku Gothic ProN", Meiryo, sans-serif !important;}
	textarea{line-height:1.7em !important;}
	tbody#listBody>tr>td>p{font-size:16px;padding-bottom:3px;line-height:1.2em;}
	tbody#listBody>tr>td{padding:2px;}
	div.zbadnotes>textarea#entertext{font-size:16px;}
	td#qaid.txt, table.zmltsh>tbody>tr>td.txt{padding-left: 1px;padding-right: 1px;width: 40px !important;text-align: center;}
}

@-moz-document url-prefix("https://contacts.skydesk.jp") {
	.grpnameUL div.vrowsel, .grpnameUL div.vrowunsel, .grpnameUL div.vrowdsel, .zcbtn div{font-size:14px;}
	.anchorlink{font-size:16px;}
	.contemail{font-size:14px;}
	.editmenubar span{font-size:14px;}
	.contName, .contmailid{font-size:20px;}
	.contName>span>a{font-size:14px;}
	.contadd legend{padding:10px 0px 2px 0px}
}
