css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(weibo.com){

/* hide */
#v6_pl_content_setskin, #v6_pl_rightmod_recominfo, #v6_pl_rightmod_recominfo, #v6_pl_rightmod_ads36, #v6_trustPagelet_recom_member, #v6_pl_rightmod_noticeboard, #v6_pl_rightmod_updatev6, div[node-type="publishTitle"], div[node-type="recommendTopic"], .gn_nav, div[node-type="feed_nav"], #v6_pl_ad_bottomtip, #plc_main > div.WB_main_r > div:nth-child(4), div[node-type="feed_spread"], .send_weibo .title_area, .nameBox, #v6_pl_content_biztips, #v6_pl_rightmod_helpcomment, #v6_pl_rightmod_ads35, #v6_pl_content_hometip, #v6_pl_rightmod_feedback, #v6_pl_rightmod_helpfav, #v6_pl_rightmod_favyoudao, .WB_innerwrap {display:none;}

/* header */

.WB_global_nav {border-top: none !important;height: 50px !important;background:rgba(0,0,0,.7) !important;}
.WB_global_nav .S_txt1, .WB_global_nav .SW_fun .S_func1 {color:#999 !important;}
.WB_global_nav .gn_set_list .W_new_count {border:none !important;}
.WB_global_nav .gn_search .W_input {background-color: rgba(0,0,0,.5) !important; border: 1px solid rgba(0,0,0,.8) !important; border-radius:50px;}
.WB_global_nav .gn_search .W_input:focus {color: #fff !important;}
.WB_global_nav .gn_logo .box .logo {background-position: 0% 40% !important; margin-left: 130px !important; width: 40px !important;}
.send_weibo {margin:0 !important; box-shadow: none !important; border-radius: 0 !important; padding: 15px 40px 0 !important;}


/* main */

.WB_feed .WB_cardwrap {margin:0 !important;}
.WB_frame {position: relative; padding:0 !important;}
.B_index .WB_frame #plc_main, .B_message .WB_frame #plc_main {width: 800px !important;}
.B_index .B_discover .WB_frame_c, .WB_main_c {width: 800px !important;}
.WB_feed_detail {padding: 20px 40px 10px !important;}
.WB_notes {margin:0 !important;}
.WB_cardwrap {border-radius:0 !important;}
.WB_tab_a .tab_box {background: #fff;}


/* left sidebar */
#v6_pl_leftnav_group {margin-top:155px !important;}
.WB_main_l, .WB_left_nav {width: 200px !important;}
.WB_left_nav .lev_line fieldset {padding: 0 0 0 160px !important;}
/* .FRAME_message .WB_main_l, .WB_left_nav {margin-left: -510px !important;}*/


/* right sidebar */
#v6_pl_rightmod_myinfo {position: absolute !important; top:0; left:0;}
.W_person_info {height: 144px !important; width:200px !important;} 
#v6_pl_rightmod_myinfo .S_bg2 {background:none !important;box-shadow:none !important;border-radius:0 !important;}
#v6_pl_rightmod_myinfo .cover {background-image:none !important;} 
.W_person_info .cover .headpic {background:none !important;}
.user_atten {font-size:12px !important; color:#fff !important;}
.user_atten a, .user_atten span {color:#fff !important;}
.user_atten li {border-color:rgba(255,255,255,.1)}
/*.W_person_info .cover .headpic img {width:100px !important; height:100px !important;}*/
.WB_main_r {position:static !important; width:0 !important;}
.W_person_info .WB_innerwrap {padding: 16px 0 9px 0 !important;}
.W_person_info .cover {height: 65px !important;}

}
