css:
@-moz-document url-prefix(http://www.tumblr.com/),url-prefix(https://www.tumblr.com/){
.identity-refresh .l-container {
padding-top: 60px;
}

.l-header-container--refresh .l-header {
min-width: 900px;
width: 100%!important;
max-width: 920px;
height: 38px!Important;
}


.l-header-container--refresh .tab_bar .tab {
margin-top: -5px;
font-size: 16px;
}


div#new_post_in_tracked_tags_bubble {
color: rgba(0,0,0,0.33) !important;
background: rgba(255,255,255,0.18);
display: inline-block;
font-size: 11px!important;
float: right;
margin-top: -26px!important;
margin-right: 5px!important;
height: 20px!important;
width: 36px!important;
line-height: 20px!important;
text-align: center;
}


ui_search .search_form_field:before {
font-size: 12px;}

.identity-refresh .tab_notice .tab_notice_value {
font-size: 10px;
}


.identity-refresh .selected .tab_notice {
background: #D95E40!important;
}

.identity-refresh .tab_notice .tab_notice_value {
color: white;}






#popover_button_blogs .hide_overflow {
padding-left: 0px;
color: rgba(255,255,255,.5);
}

.identity-refresh .tab_notice {
background: #D95E40!important;
color: white;
}



.logo.logo-redesign .svg-logo {
  padding: 8px 40px 0px 50px!important;
  height: 30px!important;
  width: auto!important;}


.header-redesign .ui_search .search_query {
  line-height: inherit!important;
  padding: 8px!important;
  padding-left: 31px!important;
  border-radius: 0!important;
  font-size: 15px!important;
  height: 36px!important;
  width: 100%!important;
  border: 0!important;
  padding-bottom: 0!important;
}


.header-redesign .ui_search .search_form_field:before {
  font-size: 29px!important;
  left: 4px!important;
  top: 2px!important;
}

.l-header-container--refresh.header-redesign.has-3d-logo .search_popover {
  top: 43px!important;
  left: 101px!important;
}

.header-redesign .compose-button {
  margin-top: 5px!important;
  z-index: 1;
  margin-bottom: 5px!important;
}

path#Shape {
  background: transparent!important;
  fill: transparent!important;
}


.dashboard-context .full-width-inline-media .post.is_regular .tmblr-full, .tmblr-full  {margin: 0!important;}
.dashboard-context .full-width-inline-media .post.is_regular .tmblr-full>img, .dashboard-context .full-width-inline-media .post.is_note .tmblr-full>img, .dashboard-context .full-width-inline-media .tmblr-full>img {min-width: 0!important; width: auto!important; max-width: 500px!important;}
.post_full .post_media_small>img.post_media_photo.image {margin: 0!important;}
.dashboard-context .full-width-inline-media .post.is_regular .tmblr-full>img {
  max-width: 100%!important;
}     
.stretchy_kids {
  display: none!important;
}
.xkit-audio-plus-slider-container{z-index:5;}
li.yamplus-unit-container.is_persistent { display: none;}

/*ul.popover_menu_list{max-height:350px!important; overflow:auto!important;}*/


a.currently_selected_blog.hide_overflow.blog_title {
    display: inline-block!important;
}

.open_blog.with_subtitle small>.hide_overflow {
    display: inline-block!important;
    width: auto!important;
    float: right!important;
}


a#open_blog_link {
    width: auto!important;
    float: right!important;
    display: inline-block!important;
    margin: 0px!important;
}
.compose-button{ margin-top: 4px!important; margin-bottom: 6px!Important;}


.left_column {
    margin-left: 50px!important;
}
div#right_column, .l-container.l-container--two-column-dashboard .right_column, .l-container.l-container--two-column .right_column {
    width: 250px!important;
    left: -30px!Important;
}
}
