div._3nzl a.jewelButton,
div._1z4y a.jewelButton,
div._4xi2 a.jewelButton,
div._59fb,
div._50__
{
	-webkit-filter: brightness(10) grayscale(1) !important;
}
div._5ahz
{
    background: transparent !important;
}
.jewelCount,
span._5ugh._3z_5
{
