/*頂部選單樣式化*/
div._1y2l,
div._3nzk,
div._4xi1,
div._8-b,
div.navigation
{
    border: 1px solid #ccc !important;
    opacity: 0.95 !important;
    box-shadow: none !important;
    border-radius: 0 !important;
}
a.navSubmenu,
label.uiLinkButton.navSubmenu,
.navSubmenu input.uiLinkButtonInput
{
    border: 0 !important;
    color: #333 !important;
    background: transparent !important;
    transition: background-color 0.1s, color 0.1s, border 0.1s !important;
}
a.navSubmenu:hover,
a.navSubmenu:focus,
label.uiLinkButton.navSubmenu:hover,
label.uiLinkButton.navSubmenu:focus,
.navSubmenu input.uiLinkButtonInput:hover,
.navSubmenu input.uiLinkButtonInput:focus
