    -webkit-border-radius:none !important; 
    border: 1px solid #555 !important; 
    background-color: #555 !important; 
    background-image: none !important;
    color:#fafafa !important; 
}
a.titlebarText
{ 
    color:#fafafa !important; 
    font-weight: normal !important;
