div._51lq
{
	-webkit-animation:fadeInUp 0.5s !important;
	animation:fadeInUp 0.5s !important;
}
/*聊天室本體淡出效果*/
.fbNubFlyout,
div.fbNub._50-v._50mz._50m-
{
    -webkit-animation:fadeIn 0.5s !important;
	animation:fadeIn 0.5s !important;
}
/*動態載入時淡出*/
div._4-u2.mbm._5jmm._5pat._5v3q._5x16
{
	-webkit-animation:fadeInUp 1s !important;
	animation:fadeInUp 1s !important;
}
/*全畫面載入時淡出效果*/
#contentCol
{
	-webkit-animation:fadeIn 1s!important;
	animation:fadeIn 1s !important;
}
/*“上傳檔案”彈出框下落動畫*/
div._1yv
{
	-webkit-animation:fadeInDown 0.5s !important;
	animation:fadeInDown 0.5s !important;
}
/*彈出圖片框下落動畫*/
div._n3
{
	-webkit-animation:fadeInDown 0.5s !important;
	animation:fadeInDown 0.5s !important;
}
/*頂部選單nav下落動畫*/
div._1y2l,
div._3nzk,
div._4xi1,
div._8-b,
div.navigation
{
    -ms-aniation:fadeInDown 0.5s !important;
	-moz-animation:fadeInDown 0.5s !important;
	-webkit-animation:fadeInDown 0.5s !important;
	animation:fadeInDown 0.5s !important;
}
/*邊欄淡化，hover效果*/
div.fbChatSidebar
{
    -webkit-filter: grayscale(0.5) opacity(0);
    transition: -webkit-filter 0.2s;
}
div.fbChatSidebar:hover
{
    -webkit-filter: grayscale(0) opacity(1);
}
/*動態時報上方fixed白條隱藏，hover效果*/
div.stickyHeaderWrap.clearfix
{
    -webkit-filter: grayscale(0.5) opacity(0);
    transition: -webkit-filter 0.2s;
}
div.stickyHeaderWrap.clearfix:hover
{
    -webkit-filter: grayscale(0) opacity(1);
}
/*keyframes*/
@-webkit-keyframes fadeIn 
{
	0% { opacity: 0; }
	100% { opacity: 1; }
}
@keyframes fadeIn 
{
	0% { opacity: 0; }
	100% { opacity: 1; }
}
@-webkit-keyframes fadeInUp
{
    0% 
    {
		opacity: 0;
		-webkit-transform: translateY(10px);
		transform: translateY(10px);
	}
    100% 
    {
		opacity: 1;
		-webkit-transform: translateY(0);
		transform: translateY(0);
	}
}
@keyframes fadeInUp
{
    0% 
    {
		opacity: 0;
		-webkit-transform: translateY(10px);
		transform: translateY(10px);
	}
    100% 
    {
		opacity: 1;
		-webkit-transform: translateY(0);
		transform: translateY(0);
	}
}
@-webkit-keyframes fadeInDown
{
    0% 
    {
		opacity: 0;
		-webkit-transform: translateY(-10px);
		transform: translateY(-10px);
	}
    100% 
    {
		opacity: 1;
		-webkit-transform: translateY(0);
		transform: translateY(0);
	}
}
@keyframes fadeInDown
{
    0% 
    {
		opacity: 0;
		-webkit-transform: translateY(-10px);
		transform: translateY(-10px);
	}
    100% 
    {
		opacity: 1;
		-webkit-transform: translateY(0);
		transform: translateY(0);
	}
}
/*---------------------------------------------------------------------動畫效果end*/
