body.page, body.popup, .titelg {
    background-image:url("http://static.allmystery.de/upics/3d33d6_pikachu_wallpaper_by_sneezepin-d53hy0t.jpg")!important;
    background-size: 100% 100% !important;
    background-color: transparent !important;
    background-attachment:fixed !important;
    }

.header {
    background-image: none !important;
    background: url(http://static.allmystery.de/upics/c67f30_Auswahl_001.png) no-repeat !important;
    background-size: 100% 100% !important;
    border-radius: 4px 4px !important; 
    border-top: 1px solid grey !important;
    border-left: 1px solid grey !important;
    box-shadow: 10px 10px 50px rgba(155,161,0,0.3) !important;
    }

.tabnav a, .in {
    background-color: rgba(0,0,0,0.7) !important;
    border-radius: 4px 4px !important; 
    box-shadow: 10px 10px 50px rgba(155,161,0,0.3) !important;
    }

.tabnav a:hover {
    background-color: transparent !important;
    box-shadow: 10px 10px 50px rgba(155,161,0,0.3) !important;
    }

a.tabnav_selected, a.tabnav_js_selected, span.highlighted {
    background-color: transparent !important;
    box-shadow: 10px 10px 50px rgba(155,161,0,0.3) !important;
    }
        
.mcell_inhalt, .mcell_left, .mcell_inhalt2, .mcell_inhalt3 {
    background-color: rgba(0,0,0,0.3) !important;
    border-radius: 2px 2px  !important;
    border-left: 1px solid grey !important;
    border-top: 1px solid grey !important;
    box-shadow: 10px 10px 50px rgba(155,161,0,0.3) !important;
    }

.mcell, .mcell2 {
    background: url(http://static.allmystery.de/upics/207646_Auswahl_002.png) no-repeat !important;
    background-size: 100% 100% !important;
    border-radius: 2px 2px  !important;
    border-left: 1px solid grey !important;
    border-top: 1px solid grey !important;
    box-shadow: 10px 10px 50px rgba(155,161,0,0.3) !important;
    }

