    border-left: 1px solid grey !important;
    border-top: 1px solid grey !important;
    box-shadow: 10px 10px 50px rgba(155,161,0,0.3) !important;
    }

div.categoryHeaderInfo {
    text-align: center !important;
    background-color: rgba(0,0,0,0.3) !important;
    border-radius: 2px 2px  !important;
    border-left: 1px solid grey !important;
    border-top: 1px solid grey !important;
    box-shadow: 10px 7px 5px rgba(95,95,95,0.3) !important;
    }

img.smallCategoryImage {
    display: none !important;
    }

.useravatar:hover {
    height: 105px !important;
    width: 105px !important;
    margin-left: -3px !important;
    transition: all 5s ease !important;
    border: 1px solid grey !important;
    box-shadow: none !important;
    }

.post, .post:nth-child(even), .paginatorLast, .paginator, .MainOverview td.Category, .MainOverview td.LastPost, td, tr, .start .subselector, .start .areas .area, .start .areas .area:nth-of-type(odd), .start .areas .area .main, .start .areas .area .last, .videos .videoitem, .videos .videoitem:nth-of-type(even) {
    background-color: rgba(0,0,0,0.3) !important;
    border-radius: 2px 2px  !important;
    border-left: 1px solid grey !important;
    border-top: 1px solid grey !important;
    box-shadow: 5px 5px 5px rgba(95,95,95,0.5) !important;
    }

.fm td, .fh td, .fd td {
    border-radius: 5px 5px  !important;
    border-left: 1px solid grey !important;
    border-top: 1px solid grey !important;
    box-shadow: 10px 7px 5px rgba(95,95,95,0.3) !important;
    }

.finfo {
    border-right: solid 1px transparent !important;
    }

.fbeitrag {
    border-left: solid 1px transparent !important;
    }

.header_info {
    background-color: rgba(0,0,0,0.7) !important;
    border-radius: 2px 2px  !important;
    border-left: 1px solid grey !important;
    border-top: 1px solid grey !important;
    box-shadow: 10px 7px 5px rgba(95,95,95,0.3) !important;
    }

