    border-radius: 3px 3px  !important; 
    box-shadow: 10px 7px 5px rgba(95,95,95,0.3) !important;
    }

.Voting-bar-filling {
    background-color: rgba(255,255,255,0.2) !important;
    border-radius: 3px 3px  !important;
    }
  
.Voting-voted .Voting-bar-filling  {
    border-radius: 3px 3px  !important;
    }

textarea {
