css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {
/* Overall Tweaks */
body { font-family: arial, sans-serif!important;
        font-family: Calibri, sans serif !important;
        line-height: 1.4em !important;
        text-align: justify!important;
}

/* =============LINKS============ */
.post .post-content a, .post .post_content a, .post .reblog-list a, .post .post_body a { color: #183b6d; border-bottom: 1px solid #596370; padding-bottom: 1px!Important; background-image: none!important; font-weight: bold;}

.l-header-container--refresh {background-color: #26394f!important;}
.l-content clearfix { margin-top: -15px!important;}
.identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container { border: none !important; }
    .identity-refresh .selected .tab_notice { background: #D95E40 !important; height: 19px !important; text-align: center !important; left: 36px !important; margin-top: 0 !important; top: -3px !important; }
.identity-refresh .tab_notice .tab_notice_value { color: #f0f0f0 !important; }
.search_form_field { background-color: #26394f!important; }

.hide_overflow { margin-left: 25px!important;}
.compose-button { visibility: hidden;}
.tab_bar tab-bar { margin-left: 100px!important;}
.notification_sentence { margin-left: -5px!important;} 

/* ======================================================= */



/* Overall Post Edits */
#content { font-family: arial, sans-serif!important;
  		text-align: justify!important; 
    	font-size: 9px!important;}
strong {
  font-weight: bold !important;
  color: #49749b !important;
}
.post_content DIV:last-child,BLOCKQUOTE {
  border-color: #cbd4dd!important;
}
.identity-refresh .post_full { -webkit-border-radius: 7px!important;
		-moz-border-radius: 7px!important;
    border-radius: 7px!important; }


/* ======================================================= */



/* Chat Post Edits */
.chat_line:nth-child(even),PRE { font-family: "lucida sans unicode";
  font-size: 11px!important;
}
.chat_line:nth-child(odd),PRE { background-color: #ddd; 
  font-family: "lucida sans unicode";
  font-size: 11px!important;
}



/* ======================================================= */


/* Monospaced Text Edits */
pre,pre a,pre SPAN,.quote_source pre,div pre,pre SPAN,pre a,CODE,pre small { 
  font:bold .8em "lucidia sans unicode",sans-serif!important;
  white-space:pre-wrap!important;
  white-space:-moz-pre-wrap!important;
  white-space:-pre-wrap!important;
  white-space:-o-pre-wrap!important;
  word-wrap:break-word!important;
  background-color: #cbd4dd!important;
  color: #49749b!important;
  letter-spacing: 1px!important;
}



/* ======================================================= */




/* Right Column Link Edits */
#right_column .controls_section li a:hover { color: #6d8ca8!important; }
#right_column .controls_section li a { letter-spacing: 1px!important; font-weight: normal!important; }


/* ======================================================= */



/* Post Activity Display Edits == Not Activity Page */
ol#posts li.notification.alt { background: rgba(57, 88, 117,.6) !important;
  border-radius: 5px!important;
  font-size: 11px;
}
ol#posts li.notification { background: rgba(57, 88, 117,.3) !important;
  border-radius: 5px!important;
  font-size: 11px;
}
ol#posts li.notification a { color: #6d8ca8!important;}



/* ======================================================= */



/* Link Posts Preview Background */
.link_text_container{
    background-color: #49749b !important;
  	
}



/* ======================================================= */


/* Post Notes Edits */
.post_notes{
  color: #6d8ca8!important;
  font-size: 6px!important;
}
.post_tags {background-color: #f6f5f7!important; margin-top: 15px!important; border-top: 1px solid #ddd!important; line-height: 2em!important;font-size: 6px!important;}
a.post_tags:hover {color: #FF0000!important;}


/* ====================================================== */


/*Xkit Bookmarks*/
.bookmark-caption {font-size: 11px!important; margin-top: -5px!important; margin-left: -10px!important;}
}
