css:
@-moz-document domain("duckduckgo.com") {
.tile,.tile__media--pr {
background-color:#2E2E2E
}

.tile h4 {
color:#E6E6E6
}

.tile__content {
color:#C2C2C2
}

.tile__title {
color:#C2C2C2
}

.tile__tx {
color:#C2C2C2
}

.info--head {
color:#E6E6E6
}

.tile--info__link {
color:#E6E6E6
}

.info__label,.info__value {
color:#E6E6E6
}

.tile--info .info {
border-top:1px solid #333
}

.at-topic .tile.active-topic {
background-color:#3E3E3E;
border-bottom:none;
color:#C2C2C2
}

.at-topic .tile.active-topic h4 {
color:#E6E6E6!important
}

.at-topic .tile {
background-color:#2E2E2E;
border:none
}

.tile__expand {
border-top:1px solid #333;
background-color:#333
}

.zci__img-wrap--logo {
border:none;
background:none
}
}
