css:
@-moz-document url("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/blog") {
.post_full.is_full_width.is_photo .post_media_photo {
height: auto;
width: 100%;
margin: 0;
padding-left: 20px;
padding-right: 20px;
}

.post_full.is_photoset .photoset .photoset_row .photoset_photo img {
/*max-width: none;*/
padding-left: 20px;
padding-right: 20px;
}

.link_button {
color: #fff;
min-height: 130px;
border-radius: 3px;
border: 1px solid #56bc8a;
background-color: #56bc8a;
-webkit-box-sizing: border-box;
-moz-box-sizing: border-box;
box-sizing: border-box;
opacity: 1;
-webkit-transition: opacity .25s linear;
-moz-transition: opacity .25s linear;
-ms-transition: opacity .25s linear;
-o-transition: opacity .25s linear;
transition: opacity .25s linear;
margin-left: 20px;
margin-right: 20px;
}

.audio_player_container.has_art {
height: 169px;
padding-left: 20px;
padding-right: 20px;
}

.post_full.is_video .crt-video {
max-height: 540px;
padding-left: 20px;
padding-right: 20px;
background: white;
}

}
