.elevator-wrapper {
top: 15px!important;
z-index:9999;
}
.elevator.visible {
background: rgba(255, 255, 255, 0.2)!important;
height: 50px!important;
width: 50px!important;
border-radius: 10px!important;
}
.elevator:before {
line-height: 68px!important;
font-size: 50px!important;
}
}
