css:
@-moz-document 
/* Googlereader
   GooderDalilV0.0
   By : Morteza Dalil
   http://sarsum.com */
url-prefix(http://www.google.com/reader),
url-prefix(https://www.google.com/reader)
{
#entries.list #current-entry.expanded .entry-actions {
border-color:#8181DC;
clear:both;
}

#current-entry.read .entry-container .entry-body {
color:#000000;
line-height:20px;
float:right;
}
.read .entry-container .entry-body {
color:#222222;
line-height:20px;

}
#entries{
background-color:#EBF2FE;
}

.entry .entry-title a{
padding:0px;
float:right;
font-size:80%;
}
.entry .entry-title .entry-title-go-to {
border:0 none;
float:right;
padding-left:0.25em;
vertical-align:top;
}
.entry .card .entry-container .entry-date {
float:right;
width:100%;
}
#current-entry.read .entry-container .entry-body {
float:right;
clear:both;
}

#entries.list .read .collapsed {
background:#EEEEEE none repeat scroll 0 0;
border:2px solid #ffffff;
}
#entries.list .entry  {
-moz-user-select:none;
background:#FFFFFF none repeat scroll 0 0;
border:4px solid #EBF2FE;
cursor:pointer;
margin:0;
overflow:hidden;
padding:1px 0;
position:relative;
width:auto;
}
#entries.list #current-entry.expanded .entry-actions {
border-color:#8181DC;
float:left;
}
#current-entry.overflow-settable .entry-main {
margin-left:26em;
overflow:auto;
}
.entry .entry-main {
float:right;
margin-left:20px;
}
.entry .entry-main {
margin-left:26em;
}
#entries.list .entry {
border-bottom:1px solid #AAAAAA;
font-family:tahoma;
font-size:11px;
margin:0;
overflow:hidden;
padding:0;
}
#entries.list .entry:hover{
-moz-user-select:none;
background:#E6E6E6 none repeat scroll 0 0;
border:2px solid #FFFFFF;
cursor:pointer;
margin:0;
overflow:hidden;
padding:3px 0;
position:relative;
width:auto;

}

#entries.list .collapsed .entry-icons {
display:block;
left:0.2em;
margin:0;
overflow:visible;
padding:0;
position:absolute;
top:1px;
width:auto;
}
#entries.list .collapsed .entry-date {
display:block;
margin:1.2em 0.7em 1.2em 0.2em;
width:auto;
}
#entries.list .collapsed .entry-main {
display:block;
float:none;
margin:0 5em 0 0;
padding:0;
}
#entries.list .collapsed .entry-main .entry-source-title {
display:block;
float:none;
left:1.85em;
margin-top:20px;
overflow:hidden;
padding:0 1em 0 0;
position:absolute;
top:1px;
white-space:nowrap;
width:13em;
}
#entries.list .collapsed .entry-secondary {
display:block;
float:none;
left:0;
margin:0 9em 0 20em;
overflow:hidden;
padding:0;
position:absolute;
top:1px;
white-space:normal;
width:auto;
}
#entries.list .collapsed .entry-secondary .entry-title {
display:inline;
font-family:tahoma;
font-family:tahoma;
font-size:12px;
margin:0;
padding:0;
position:static;
white-space:inherit;
width:auto;

}
#entries.list .collapsed .entry-secondary .snippet {
display:inline;
float:none;
margin:0;
padding:0;
position:static;
white-space:inherit;
width:auto;
}
#current-entry.read .entry-container .entry-body {
color:#000000;
float:right;
margin-right:8em;
}

.entry .card .c {
background-color:#FFFFFF;
background-repeat:no-repeat;
border:0 none;
font-family:tahoma;
padding:0;
}
#sub-tree {
direction:rtl;
font-family:tahoma;
font-size:10px;
height:420px;
overflow-x:hidden;
overflow-y:auto;
width:255px;
}
#current-entry.overflow-settable .entry-main {
overflow:auto;
}

}
