css:
@-moz-document url-prefix("https://www.tumblr.com/dashboard"),url-prefix("https://www.tumblr.com/blog/"),url-prefix("https://www.tumblr.com/tagged/") {

div[style*="width: 540px;"] {
	margin:0!important;
	width:500px!important;
}
.embed_iframe,.photoset_row.photoset_row_1,#posts .post_container,#posts .post_container > div {
	width:500px!important;
}

.post_full.is_full_width.is_photoset .photoset .photoset_row,.post_full.is_full_width.is_photo .photoset .photoset_row {
	margin-top:10px;
}

.photoset_row.photoset_row_1 {
	height:auto!important;
}

.photoset_row.photoset_row_2 {
	width:500px!important;
	height:auto!important;
}

.photoset_row.photoset_row_3 {
	height: auto !important;
}

.photoset_row.photoset_row_3 img {
	width:164px!important;
}

img[style*="width:268px;"] {
	width:245px!important;
}

.l-container--two-column-dashboard #posts .post_container.new_post_buttons_container {
width: auto !important;
}
.l-container--two-column-dashboard #posts .post_container.new_post_buttons_container > div {
	width:540px!important;
}
.post_full.is_full_width.is_photoset .photoset .photoset_row .photoset_photo {
	margin-left:10px;
}

.post_full.is_photoset .photoset .photoset_row .photoset_photo img {
	max-width:500px!important;
}

.post .post_content img {
	max-width: 100%  !important;
	height: auto !important;
}
.post_full.is_full_width.is_photo .post_media_photo[style*="width: 540px;"] {
	width:100%!important;
	height:100%!important;
}

}

@-moz-document  url-prefix() {
body[data-page-root*="/indash_blog/peepr/"] .indash_blog .post_content img[style="width: 540px; height: 540px;"] {
  width: 500px !important; 
}
}
