body {

background: url('http://i.imgur.com/35AT13a.jpg') center right #000000 repeat fixed !important;
  
}

.identity-refresh .selected .tab_notice {
        background: #42C7A9;
      }

.compose-button {
      color: rgba(54, 93, 86, 0.8);
      border-color: #42C7A9;
      background: #42C7A9;
        }

.compose-button:active {
  background-color: #3BB096;
  border-color: #3BB096;
  color: rgba(54, 93, 86, 0.8);
    }
      
.identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container {
            border: 0px;
        	border-radius: 4px;
        	min-width: 21px;
            height: 18px;
          }

.identity-refresh .tab_notice .tab_notice_value {
                  color: rgba(54, 93, 86, 0.8);
                }
.l-header-container--refresh {
                    background-color: rgba(30, 44, 39, 0.44);
                  }

.identity-refresh .l-container.l-container--two-column .l-content, .identity-refresh .l-container.l-container--two-column-dashboard .l-content {
          background-color: rgba(30, 44, 39, 0.11);
        }

.identity-refresh .l-container.l-container--two-column-dashboard .left_column {
  background-color: rgba(0, 0, 0, 0);
                }
        
.identity-refresh .l-container.l-container--two-column .right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column {
                  background-color: rgba(30, 44, 39, 0.11);
        }

.identity-refresh .controls_section li:not(.section_header):active {
        background-color: rgba(11, 14, 18, 0.33);
      }

.identity-refresh .controls_section li:not(.section_header):hover {
              background-color: rgba(11, 14, 18, 0.33);
      }

.identity-refresh .post_full .post_permalink {
                    border-color: #1F2022 #1F2022 transparent transparent;
                  }
.identity-refresh .controls_section.user_list li .follow_list_item_blog:before {
                            background-color: rgba(0, 0, 0, 0);
                            background-image: none;
                            border-right: none;
                          }

.identity-refresh .radar .radar_footer .radar_avatar:before {
                                      background-image: none;
                                      background: rgba(0, 0, 0, 0);
                                      background-color: rgba(0, 0, 0, 0);
                                    }

.radar .radar_footer .radar_avatar:before {
                                      background: rgba(0, 0, 0, 0);
                                	  background-color: rgba(0, 0, 0, 0);
                                      background-image: none;
                                            }

.identity-refresh .right_column:after {
                                      background: none;
	}

.identity-refresh .controls_section.user_list li:hover .follow_list_item_blog:before {
                                              background: none;
                                              background-image: none;
                                              background-color: none;
                                              border-right: none;
	}

.identity-refresh .right_column .follow:hover {
	background: #42C7A9;
	color: #FFF;
    }

.identity-refresh .right_column .follow {
	color: #222826;
	}

.identity-refresh .post_avatar {
      background-color: rgba(13, 13, 13, 0.1);
    }

.notifications_page .section.divider {
        background: none;
      }
}
