css:
@-moz-document domain("youtube.com") {

#body-container {
    padding-bottom: 0px !important;
}

.ytp-settings-hd-quality-badge {
    display: none;
}
  

/* whole upperleft */  
#yt-masthead-user {
    display: none;
}
/*upload button */
#appbar-onebar-upload-group {
    display: none;
}
/* top right bell */
.yt-uix-button-icon-bell {
    display: none;
}
/* top right user icon */
#yt-masthead-account-picker {
  	display: none;
}


#search-btn {
    display: none;
}
/* comments */
#watch-discussion {
    display: none;
}
#watch8-action-buttons {
  	display: none;
}
.yt-uix-button-subscribe-branded {
  	background-color: #f1f1f1;
}
.yt-uix-button-subscribe-branded:hover {
  	background-color: #f0f0f0;
}
#footer-container, #yt-uix-videoactionmenu-menu {
    display: none !important;
}
.yt-uix-button-subscribe-branded .yt-uix-button-icon-subscribe {
  	display: none !important;
}
.yt-uix-button-subscribe-branded+.yt-uix-subscription-preferences-button+.yt-subscription-button-subscriber-count-branded-horizontal {
    display: none !important;
}
.yt-uix-button-subscribe-branded {
    border: 1px solid #ccc !important;
    background-color: #f8f8f8 !important;
    color: #666 !important;
}
/* center "Subscribe" text in the button on a video */
.yt-uix-button-icon-wrapper {
  	margin-right: 3px !important;
}
#feed {
    display: none;
}

#header {
    display: none;
}
#ad_creative_1, .pyv-afc-ads-container {
 	display: none;
}


/* don't display "add to watch later" icon over videos in side bar */
.addto-watch-later-button {
    display: none !important;
}
a:hover .yt-uix-simple-thumb-wrap .video-time {
	display: inline-block !important;
}

#share-services-container {
    display: none;
}
    
    
 
/* turn progress bar white */
.html5-play-progress, .ytp-play-progress {
    background: #F5F5F5 !important; 
}
.html5-scrubber-button:hover, .ytp-scrubber-button:hover {
    background: #F5F5F5 !important;
}
.ytp-volume-slider-foreground:before {
    background: #F5F5F5 !important;
}
.html5-progress-bar:focus .html5-scrubber-button {
    background: #F5F5F5 !important;
}

}
