css:
@-moz-document domain("habitrpg.com") {
div.habits h2,
div.dailys h2, 
div.todos h2,
div.rewards h2 {
  background-repeat: no-repeat;
  background-size: auto 100%;
  background-position: center;
  text-indent: 100%;
  white-space: nowrap;
  overflow: hidden;
}

div.habits h2 { background-image: url('https://i.imgur.com/wgQeqNW.png'); }
div.dailys h2 { background-image: url('https://i.imgur.com/MJIoGv0.png'); }
div.todos h2 { background-image: url('https://i.imgur.com/16VcHyd.png'); }
div.rewards h2 { background-image: url('https://i.imgur.com/TzlPOvi.png'); }

li.daily div markdown p a img {
  max-width: 36px;
  max-height: 36px;
  border-image:url("https://i.imgur.com/RhpmxN7.gif") 8 8 round;
  border-bottom-width: 4px;
  border-right-width: 4px;
  border-left-width: 4px;
  border-top-width: 4px;
  float: left;
  margin-right: 4px;
}

li.daily div markdown p a img:hover {
  max-width: 96px;
  max-height: 96px;
  border-bottom-width: 8px;
  border-right-width: 8px;
  border-left-width: 8px;
  border-top-width: 8px;
  margin-bottom: 4px;
}

ul.items.rewards {
  background-image: url("https://i.imgur.com/ZlUkBK4.png");
  background-size: auto 24px;
  background-position: bottom center;
  background-repeat: no-repeat;
}
}
