css:
@-moz-document url("https://www.tumblr.com/dashboard"), url("http://www.tumblr.com/dashboard"), url("https://tumblr.com/dashboard"), url("http://tumblr.com/dashboard"), url-prefix("http://tumblr.com/blog"), url-prefix("https://tumblr.com/blog"), url-prefix("http://www.tumblr.com/blog"), url-prefix("https://www.tumblr.com/blog") {
.post_container {
    box-shadow: 0px 2px 5px rgba(0,0,0,0.6);
    /*width: 548px !important;*/
}

/* ### Working on adding borders back at a later time. Sorry :(
.post_wrapper, .post_header, .post_footer {
    min-width: 548px !important;
    background-color: #fff !important
}*/

.post_avatar {
    box-shadow: 0px 4px 8px rgba(0,0,0,0.6);
}
.post_avatar_link {
    background-color: rgba(0,0,0,0.4) !important;
}

.l-header-container {
    background-color: #263242;
    background: linear-gradient(rgb(38,50,66), rgb(38,50,66), rgb(38,50,66), rgb(33,42,56));
    border-bottom: 1px solid rgb(74,96,128);
    box-shadow: none !important;
    position: absolute !important;
}

.right_column {
    background-color: rgba(0,0,0,0.2) !important;
    padding: 8px;
    margin-top: -1px !important;
    border-radius: 4px;
    box-shadow: inset 0px 0px 5px rgba(0,0,0,0.33);
}
.right_column::after {
    background: linear-gradient(to bottom,#303e53,rgba(54,70,93,0)) !important;
}

.post_media {
    /*border: 4px solid #fff;*/
    min-width: 100%;
    max-width: 100%;
}

.elevator-wrapper, .elevator {
    width: 64px;
    height: 64px;
    line-height: 64pt;
    font-size: 48pt;
}
  
.l-container, .l-content, .left_column {
    background-color: #303e53 !important;
    border-radius: 0 !important;
}
.l-container {
    box-shadow: inset 0px 0px 8px rgba(0,0,0,0.2);
}

.ui_peepr_wrapper {
    box-shadow: 0px 0px 48px rgba(0,0,0,0.7);
}

.settings_group_wrapper {
    background-color: #fff;
    padding-bottom: 16px !important;
    border-bottom-left-radius: 4px;
    border-bottom-right-radius: 4px;
}

.controls_section.user_list li .follow_list_item_blog:before, .radar .radar_footer .radar_avatar:before {
    background: transparent !important;
    border: 0 !important;
}
        
.popover_blogs {
    margin-top: 40px !important;
}
          
.post_content_inner .post_container {
    box-shadow: none;
}

.tab_notice {
    background: #D95E40 !important;
}
.inbox_notice_container, .new_post_notice_container {
    border: 2px solid #D95E40 !important;
}
.tab_notice .tab_notice_value {
    color: #fff !important;
    text-shadow: 0px 1px 1px rgba(0,0,0,0.5);
    font-size: 8pt !important;
}
}
