/* Xkit */

#xkit-control {

    padding-right: -17px !important;
}


#xkit-control a.tab_anchor {

    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 36px !important;
    padding-top: 40px !important;
    background: url('https://31.media.tumblr.com/909f7d2d0132f13c055ab5566d32f8d5/tumblr_inline_necbi9dNjD1qc0er4.png') !important;}

/* Log Out */

.tab.iconic.tab_log_out a{

    padding-right: -17px !important;
}


.tab.iconic.tab_log_out a {

    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 36px !important;
    padding-top: 40px !important;
    background: url('https://31.media.tumblr.com/af62706f24d5f5be54e5693388d08a27/tumblr_inline_necbj2XUCL1qc0er4.png') !important;}


/*Positioning*/

.tab_bar .iconic a.tab_anchor {background-size: 175px 36px;}

.tab_bar .tab_discover .tab_anchor {background-position: -175px 0}

.tab_bar .tab_log_out .tab_anchor {background-position: -140px 0}

.tab_bar .tab_settings .tab_anchor {background-position: -105px 0}

.tab_bar .tab_help .tab_anchor {background-position: -70px 0}

.tab_bar .tab_inbox .tab_anchor {background-position: -35px 0}

.tab_bar .tab_home .tab_anchor {background-position: 0 0}

.tab_bar .tab.iconic:hover .tab_anchor,.tab_bar .tab.iconic:active .tab_anchor {opacity: .7;}

.tab.iconic > .tab_anchor::before {content: none !important;}

}
