css:
@charset "UTF-8";
@namespace url(http://www.w3.org/1999/xhtml);

/* 
Författare: Thomas Jedenfelt, 2014-11-01
http://userstyles.org/user/show/117

Se även "SMHI väderprognos - Högkontrast (test)"
http://userstyles.org/styles/106864/
*/

@-moz-document url-prefix("http://www.smhi.se/vadret/vadret-i-sverige/land") {

DIV#head,
DIV#leftmenu,
DIV.wCard33,
div.columnsplitter,
DIV.element,
DIV.divider_black_bottom,
DIV.bottom,
DIV#footer {
  display: none !important;
  }

DIV.w100 {
  margin-left: 1em !important;
  width: auto !important;
  }

DIV#container {
  width: auto !important;
  }

DIV.heading H1 {
  font-size: 250% !important;
  font-weight: bold;
  margin-top: .3em !important;
  }

DIV#content {
  width: auto !important;
  }

SPAN.morePrognoses {
  float: none !important;
  margin-left: -1.5em !important;
  position: absolute !important;
  left: 0em !important;
  top: -2em !important;
  }

DIV#ajax-changeCity {
  position: absolute !important;
  left: 25em !important;
  top: -1.7em !important;
  width: 18em !important;
  }

DIV#navigationDefault_navigation {
  width: auto !important;
  }

DIV.wData {
  position: absolute !important;
  left: 1em !important;
  }

DIV#warningFrame {
  border: none !important;
  }

DIV.container {
  margin-left: 0em !important;
  }

DIV.container P {
  margin-top: -1em !important;
  font-style: italic !important;
  }

DIV.container P#showObservations {
  position: absolute !important;
  top: 1em !important;
  left: 30em !important;
  width: auto !important;
  }

DIV.container A#pp_kunskapsbanken {
  font-weight: bold;
  margin-left: 0em !important;
  margin-bottom: 5em !important;
  }

TABLE.forecast-city-detail {
  margin-top: 1em !important;
  width: auto !important;
  }

TABLE.forecast.forecast-city-detail td[colspan="4"] {
  white-space: nowrap !important;
  }

TD.wind-symbol,
TD.wind-symbol IMG {
  background: white !important;
  width: 20px !important;
  margin-right: 1em !important;
  }

TD.wind-value SPAN.value {
  margin-left: 1em !important;
  }

TD.air-pressure SPAN.unit {
  margin-left: .3em !important;
  }

}
