@-moz-document domain("eow.alc.co.jp") {
  p.pr_text { display: none;}
  div#AreaUpperRight { display: none;}
  div#AreaHeaderLeft { display: none;}
  div#eijiro_img { display: none;}
  div#sw {
    position: absolute;
    left: 0;
    top: 0;
    padding: 5px;
