css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("neopets.com"){

/*spacer*/
.content>table>tbody>tr>td[width="7"],
/*ads*/
[class^="ad_"],
.content DIV[style="border: 2px solid rgb(250, 250, 250); padding: 0pt; float: left; margin-right: 8px;"],
#main>#ban,
body>#ban_bottom
{display:none!important}

/*resize*/
.content>table>tbody>tr>td{width:50%!important}
#boards_table{width:100%!important}
#main>div,
#main{width:auto!important}

/*align posts to left*/
#boards_table .topic>div{text-align:left!important}
#boards_table .topic>div>font[color="#ff0000"]{display:block!important;text-align:center!important}

/*re-colour*/
select,
input{-moz-appearance:none!important;color:#fff!important;background-color:#000!important}a{color:#00ff00!important}

[color="green"],
[style*="color: green"]{color:lightgreen!important}

.sidebar,
td,
.neofriend,
.activePetInfo,
.content .medText,
.contentModuleContent,
.contentModuleContent [style^="background-color"],
.content [bgcolor],
.contentModuleHeaderAlt,
body{background-color:#000!important;color:#fff!important}

#template_nav *,
.sidebarHeader,
#footer .copyright>a,
#template_nav{background-color:#aaa!important;border-color:#fff!important}

#main,
#footer *,
#footer,
#header{background:transparent!important;border-color:#999!important}

.sidebarHeader.medText{color:#000!important}
.content .medText,
#header *,
#footer *{color:#fff!important}
}

@-moz-document url-prefix("http://www.neopets.com/neoboards/boardlist.phtml?board="),url-prefix("http://neopets.com/neoboards/boardlist.phtml?board="){
.content .blistSmall>div{background-color:#000!important;text-align:center!important}
}
