css:
@-moz-document domain("emirs.miet.ru") {
body {
  font-family: "Open Sans", "Segoe UI", sans-serif;
  background: white;
}

a.menu {
  font-family: "Open Sans", "Segoe UI", sans-serif!important;
  font-size: 13px!important;
  font-weight: 400!important;
  color: #777;
}

a.menu:hover {
  text-decoration: none!important;
  color: #3498db;
}

a.menu:active {
  color: #2980b9!important;
}

table td {
  background: white;
}

table[cellpadding="0"] tbody>tr[height="30"],
table[rules="COLS"],
table[width="20%"],
center>b {
  display: none;
}

table[rules="GROUPS"] {
  background: white;
}

table[rules="GROUPS"] thead {
  display: none;
}

table[rules="GROUPS"] caption {
  margin-bottom: 16px; 
}

table[rules="GROUPS"] caption>b {
  font-size: 32px;
  font-weight: 100;
}

td td td {
  position: relative;
  padding: 7px;
}

font[size="1"] {
  font-size: 16px;
  color: #444;
}

font[color=green] {
  color: #111;
}

font[color=green] b, font[color=red] b {
  font-size: 18px;
  font-weight: 300;
}

input[type=image] {
  position: absolute;
  top: 0;
  left: 0;
  padding: 17px;
  padding-right: 2000px;
  overflow: hidden;
  width: 0;
  height: 0;
  z-index: 1000;
  background: rgba(63, 86, 116, 0.46);
}

input[type=image]:hover {
	background: rgba(63, 86, 116, 0.58);
}

input[src$="dir1a.gif"], input[src$="dir2b.gif"] {
  border-top: 1px solid rgba(0,0,0,0.05);
}
input[src$="doc.gif"] {
  padding-top: 16px;
  padding-bottom: 16px;
  margin-left:-30px;
  background: rgba(63, 86, 116, 0.23);
  border-bottom: 1px solid rgba(0,0,0,0.05);
}

input[src$="doc.gif"]:hover {
  background: rgba(63, 86, 116, 0.33);
}

img[src$="dir3b.gif"] {
  width: 0px;
}

img[src$="dir4b.gif"] {
  width: 0px;
  height:19px;
}

table {
  overflow: hidden;
}

img[align=middle] {
  vertical-align: middle;
}

form[name=uform] {
  max-width: 900px;
  margin-top: -10px;
  border: 1px solid rgba(0,0,0,0.1);
  border-top: none;
  padding: 0 16px;
  border-radius: 3px;
}

input[type=radio] {
  margin-top: 0px;
  vertical-align: middle;
}

form[name=uform]>table {
  color: #555;
}

form[name=uform]>table td {
  border-bottom: 1px solid rgba(0,0,0,0.1);
}

form[name=uform]>table td[align=right] {
  display: none;
}

hr {
  border: 1px dashed #eee;
}

div[align=center] {
  padding-top: 1px;
  text-align: left;
}

input[type=submit] {
  padding: 7px 9px;
  background: #3498db;
  border: none;
  border-radius: 3px;
  cursor: pointer;
  color: white;
  outline: none;
}

input[type=submit]:hover {
  background: #2a8ac8;
}

input[type=submit]:active {
  background: #2980b9;
}

input[name=return] {
  background: #95a5a6;
}

input[name=return]:hover {
  background: #929fa0;
}

input[name=return]:active {
  background: #7f8c8d;
}


input[type=text],
input[type=password],
input[name=answer] {
  padding: 6px;
  border: 1px solid #999;
  border-radius: 3px;
}

input[name=answer]:active,
input[name=answer]:focus {
  border: 1px solid #555;
  outline: none;
}
}

@-moz-document url("http://emirs.miet.ru/oroks-miet/spisok.shtml") {
center {
  margin: -10px auto 0;
	width: 700px;
}
big {
  text-transform: uppercase;
  font-size: 22px;
}

table {
  border: none!important;
}

li {
  list-style: none;
}

li>a>font {
  font-size: 16px;
}

li>a:hover>font {
  color: rgb(19, 110, 234);
}

a[href$="49"] font {
  font-size: 18px;
  text-transform: uppercase;
  text-decoration: underline;
}
}
