css:
@-moz-document regexp("https*://www.amazon.(ca|cn|co.jp|co.uk|com|com.au|com.br|com.mx|de|es|fr|in|it|nl).*") {
header
{
height:128px!important;
}
div#navbar
{
background-color:white!important;
position:fixed!important;
width:100%!important;
margin:0px!important;
}
div#rightResultsATF div#atfResults img,
div#rightResultsATF div#btfResults img,
div#rightResultsATF div#mainResults img,
div#search-results div#atfResults img,
div#search-results div#btfResults img,
div#search-results div#mainResults img
{
height:50%!important;
width:50%!important;
}
div#rightResultsATF li.ilo3 a
{
line-height:1em!important;
height:auto!important;
width:auto!important;
}
div#rightResultsATF li.ilo2 div,
div#rightResultsATF li.ilo3 div
{
margin:0px!important;
width:150px!important;
}
div#rightResultsATF li.ilo2
{
margin:0px 10px 10px 0px!important;
}
div#rightResultsATF div#atfResults div.s-item-container,
div#rightResultsATF div#btfResults div.s-item-container,
div#rightResultsATF div#mainResults div.s-item-container,
div#rightResultsATF li.ilo3
{
padding:10px!important;
}
table#dmusic_tracklist_content tr[id^="dmusic_tracklist_player_row_"] th,
table#dmusic_tracklist_content tr[id^="dmusic_tracklist_player_row_"] td,
table#dmusic_tracklist_content tr[id^="dmusic_tracklist_player_row_"]
{
height:35px!important;
margin:0px!important;
padding:0px!important;
}
div#dv-episode-list div.dv-episode-container div.dv-el-title-data
{
margin:0px!important;
padding:0px!important;
}
div#dv-episode-list div.dv-episode-container span.dv-action-purchase-button
{
font:10px Arial!important;
white-space:nowrap!important;
}
div.s-result-list div.s-result-item div.s-image-fixed-height
{
height:128px!important;
}
div#browse-grid-view div.browse-grid-view-item-unit
{
height:28rem!important;
}
div.s-result-list div.s-result-item div.s-music-cart-container,
div.s-result-list div.s-result-item img.s-sponsored-info-icon,
div.s-result-list div.s-result-item span.a-button-oneclick,
div.s-result-list div.s-result-item span.a-button-preorder,
div.s-result-list div.s-result-item span.a-button-primary,
div.s-result-list div.s-result-item div.a-spacing-top-mini div.a-spacing-top-micro,
div#atfResults div.s-item-container div.s-music-cart-container,
div#atfResults div.s-item-container img.s-sponsored-info-icon,
div#atfResults div.s-item-container span.a-button-oneclick,
div#atfResults div.s-item-container span.a-button-preorder,
div#atfResults div.s-item-container span.a-button-primary,
div#btfResults div.s-item-container div.s-music-cart-container,
div#btfResults div.s-item-container img.s-sponsored-info-icon,
div#btfResults div.s-item-container span.a-button-oneclick,
div#btfResults div.s-item-container span.a-button-preorder,
div#btfResults div.s-item-container span.a-button-primary,
div#mainResults div.s-item-container div.s-music-cart-container,
div#mainResults div.s-item-container img.s-sponsored-info-icon,
div#mainResults div.s-item-container span.a-button-oneclick,
div#mainResults div.s-item-container span.a-button-preorder,
div#mainResults div.s-item-container span.a-button-primary,
div#mainResults ul.rsltL li span.a-button,
div#dv-episode-list div.dv-el-packshot,
div#content div#sc-new-upsell,
div#content div#cart-upsell,
div#search div[data-component-type="sp-sponsored-result"],
div#nav-upnav
{
display:none!important;
}
}
@-moz-document regexp("https*://www.amazon.(ca|cn|co.jp|co.uk|com|com.au|com.br|com.mx|de|es|fr|in|it|nl)/clouddrive/.*") {
header
{
height:56px!important;
}
}
@-moz-document regexp("https*://www.amazon.(ca|cn|co.jp|co.uk|com|com.au|com.br|com.mx|de|es|fr|in|it|nl)/mn/dcw/myx.*"), regexp("https*://www.amazon.(ca|cn|co.jp|co.uk|com|com.au|com.br|com.mx|de|es|fr|in|it|nl)/hz/mycd/myx.*"), regexp("https*://www.amazon.(ca|cn|co.jp|co.uk|com|com.au|com.br|com.mx|de|es|fr|in|it|nl)/gp/digital/fiona/manage.*") {
div#contentAction_delete_myx,
div#contentAction_dummy_del_myx
{
display:none!important;
}
div.contentContainer_myx label.listViewIconPosition_myx
{
height:20px!important;
vertical-align:50%!important;
}
div.contentContainer_myx div.contentTableListRow_myx *
{
margin-top:auto!important;
margin-bottom:auto!important;
padding-top:inherit!important;
padding-bottom:inherit!important;
white-space:nowrap!important;
}
div.contentContainer_myx div.contentTableListRow_myx:nth-child(2n+1)
{
background:#eeeeee!important;
}
div.contentContainer_myx hr.contentTableListRow_myx
{
border-color:#333333!important;
}
}
@-moz-document regexp("https*://www.amazon.(ca|cn|co.jp|co.uk|com|com.au|com.br|com.mx|de|es|fr|in|it|nl)/gp/dmusic/cloudplayer/.*"), regexp("https*://music.amazon.(ca|cn|co.jp|co.uk|com|com.au|com.br|com.mx|de|es|fr|in|it|nl)/.*") {
div#tableContainer table.tableList,
div#tableContainer table.tableList tr.selectable,
div#tableContainer table.tableList tr.selectable *
{
line-height:1em!important;
height:auto!important;
}
div#page-wrapper div.dragSongArea,
div#page-wrapper div.dragSongArea div#main-content-wrapper
{
width:auto!important;
}
div#tableContainer div.actionButtons a.buttons[href^="#remove/"],
div#optionPanel ul li a.optionPanelLink[href^="#remove/"],
div#globalRactiveContainer section.superSelectBar span.removeFromPlaylistButton,
div#dragonflyTransport div.rightSide span.rightControls div.listViewContextButton,
div#dragonflyTransport div.rightSide span.rightControls span.listViewStatusButtonInLibrary,
div#dragonflyView div.headerActions span.button[aria-label*="削除"],
div#dragonflyView div.headerActions span.removeFromLibrary,
div#dragonflyView div.headerActions span.playListHeaderContextMenu,
div#dragonflyView div.listViewContextButton,
div#dragonflyView table.playlistDetailsListTable tr.playlistDetailsListItem td.libraryStatus,
div#contextMenuOverlay music-list-item[primary-text*="Remove"],
div#contextMenuOverlay music-list-item[primary-text*="削除"],
div#page-wrapper div#content-messaging,
div#page-wrapper div#right-content-panel
{
display:none!important;
}
}
@-moz-document regexp("https*://www.amazon.(ca|cn|co.jp|co.uk|com|com.au|com.br|com.mx|de|es|fr|in|it|nl)/gp/video/library.*"), regexp("https*://www.amazon.(ca|cn|co.jp|co.uk|com|com.au|com.br|com.mx|de|es|fr|in|it|nl)/gp/video/mystuff/library.*") {
div#aiv-cl-main-middle span._1VitaD,
div#aiv-cl-main-middle a._1NNx6V,
div#aiv-cl-main-middle div.dv-tile-actions
{
display:none!important;
}
}
@-moz-document regexp("https*://www.amazon.(ca|cn|co.jp|co.uk|com|com.au|com.br|com.mx|de|es|fr|in|it|nl)/gp/mas/your-account/myapps.*") {
div#masrw-my-apps table.masrw-table tr,
div#masrw-my-apps table.masrw-table tr th *,
div#masrw-my-apps table.masrw-table tr td:not([class="masrw-page-picker"]) *
{
line-height:1em!important;
height:auto!important;
margin:0px!important;
padding:0px!important;
}
div#masrw-my-apps table.masrw-table tr button.a-button-text
{
margin:4px!important;
}
div#masrw-my-apps table.masrw-table tr td div.masAppImage img
{
height:30px!important;
width:30px!important;
}
div#masrw-my-apps table.masrw-table tr th:nth-child(n+2),
div#masrw-my-apps table.masrw-table tr td:nth-child(n+2)
{
width:10%!important;
}
div#masrw-my-apps table.masrw-table tr td p.masAppDetailsArchived,
div[id^="a-popover-content"] ul li a.a-link-normal[id^="deleteAppID"]
{
display:none!important;
}
}
