css:
@-moz-document url("https://www.tumblr.com/"), url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/likes"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/inbox"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/tagged/"), url("https://tumblr.com/dashboard"), url("https://tumblr.com/likes"), url("https://tumblr.com/following"), url("https://tumblr.com/inbox"), url-prefix("https://tumblr.com/blog/"), url-prefix("https://tumblr.com/tagged/"), url-prefix("https://tumblr.com/") {

::selection {background-color:transparent;color: #32536b;}
::-moz-selection {background-color:transparent;color: #32536b;}
::-webkit-selection {background-color:transparent;color: #32536b;}

body {
font-family: "Helvetica Neue","HelveticaNeue",Helvetica,Arial,sans-serif;
font-weight: 400;
line-height: 1.4;
font-size: 14px;
font-style: normal;
color: #789db6;
}

.identity-refresh {
background-color: #789db6;
background-image: url("http://i.cubeupload.com/WcGG6V.png");
}

.identity-refresh .l-container.l-container--two-column-dashboard .left_column {
background-color: transparent;
}

.identity-refresh .l-container.l-container--two-column-dashboard .l-content {
background-color: transparent;
}

.identity-refresh .post_full .post_permalink {
border-color: transparent transparent transparent transparent;
border-radius: 0 0 0 5px;
border-style: solid;
position: absolute;
border-width: 2px;
background: rgba(255,255,255,0.5);
}

.post.post_full {
background: rgba(255,255,255,0.5);
}
    
.post .post_content {
background: transparent;
}

.post_full .post_header .post_info .post_info_link {
color: #32536b;
text-shadow: 1px 1px 0px #d1e8f8;
}

a {
color: #32536b;

}

.post_full .post_tags .post_tag {
color: #789db6;
text-shadow: 1px 1px 0px #d1e8f8;
}

.post .post_tags .post_tag:hover {
color: #32536b;
text-shadow: 1px 1px 0px #d1e8f8;
}

.l-header-container--refresh {
background-color: rgba(255,255,255,0.5);
  background-image: url('http://i.cubeupload.com/C09J7U.png');
    opacity:.5;
}

.l-header-container--refresh .logo .logo_anchor {
background-image: url('http://i.cubeupload.com/wDR8DH.png');
      }

.compose-button {
color: #789db6;
border-color: rgba(255,255,255,0.5);
background: rgba(255,255,255,0.5);
}
  
  .post_full .post_footer {
color: #789db6;
}
      
      .notification .notification_sentence {
color: #789db6;
}
          
          .notification .notification_sentence a.username {
color: #789db6;
}
              
              .identity-refresh .l-container.l-container--two-column .right_column, .identity-refresh .l-container.l-container--two-column-dashboard .right_column {
background-color: rgba(255,255,255,0.5);
}
              
              .identity-refresh .right_column:after {
background: -webkit-linear-gradient(top,transparent,rgba(54,70,93,0));
background: linear-gradient(to bottom,transparent,rgba(54,70,93,0));
}
                
                .identity-refresh .controls_section .currently_selected_blog {
color: #789db6;
}

div, span, applet, object, iframe, h1, h2, h3, h4, h5, h6, p, blockquote, pre, a, abbr, acronym, address, big, cite, code, del, dfn, em, img, ins, kbd, q, s, samp, small, strike, strong, sub, sup, tt, var, b, u, i, center, dl, dt, dd, ol, ul, li, fieldset, form, label, legend, table, caption, tbody, tfoot, thead, tr, th, td, article, aside, canvas, details, embed, figure, figcaption, footer, header, hgroup, menu, nav, output, ruby, section, summary, time, mark, audio, video {
color: #789db6;
}

.identity-refresh .controls_section li.controls_section_item>a:first-of-type, .identity-refresh .controls_section li.controls_section_item>div:first-of-type:hover {
background-color: transparent;
}

}
