css:
@-moz-document url-prefix("https://www.youtube.com/watch"), url-prefix("http://www.youtube.com/watch") {
/* WIDE VIDEO FOR 1280 */

#body-container #player,
        #body-container #player-api {
            width: 1000px !important;
height: 600px !important;
            max-width: 1000px !important;
max-height: 600px !important;
        }
        
       #body-container .html5-main-video {
            height: 100% !important;
            width: 100% !important;
        }
              

/* TOP FRAME FORMATING */

#masthead-search, #masthead-positioner {
    margin-left: auto !important;
    margin-right: auto !important;
}

#yt-masthead-container {
    border-bottom: 0px !important;
}


/* LOGO */

#logo {
    opacity: .40 !important;
    filter: alpha(opacity=0) !important;
}

#logo:hover {
    opacity: 1 !important;
}

#logo:active {
    opacity: 0 !important;
}


/*  SEARCH FORMATING  */

#yt-masthead-content {
    padding-top: 5px !important;
    padding-right: 150px !important;

}

#masthead-search-terms {
    border-radius: 9px !important;
    color: #cecece !important;
}

#masthead-search-term {
    color: #cecece !important;
}

.search-button:hover, .yt-uix-button-content:hover {
    opacity: 1 !important;
}

.search-button:active, .yt-uix-button-content:active {
    opacity: 0 !important;
}

.yt-uix-button-default,.yt-uix-button-text{
 	 border-color:#181818 !important;
      background:#181818 !important;
}

.yt-uix-button-content {
	background: none !important;

}

.search-button {
    opacity: .40 !important;
    filter: alpha(opacity=0) !important;
    background-size: auto !important;
    width: 39px !important;
    height: 28px !important;
    margin: 0 0 0 0 !important;
    opacity: .40 !important;
    filter: alpha(opacity=0) !important;
  background: no-repeat url(http://s.ytimg.com/yts/imgbin/www-hitchhiker-vflUkjRR6.png) -5px -349px !important;
    margin-left: 20px !important;
}

#masthead-search-terms {
    -moz-transition: border-color 1.2s ease;
    -webkit-transition: border-color 1.2s ease;
    transition: border-color 1.2s ease;
}

/* CLEAR BUTONS */

.yt-uix-button-subscribed-branded, .yt-uix-button-subscribe-branded, .yt-uix-subscription-preferences-button, .yt-subscription-button-subscriber-count-branded-horizontal { 
background: none !important;
border: 0px!important;
}


/*  EXPAND DESCRIPTION  */
.yt-uix-expander-collapsed, #watch-description-text, #watch-related {
    max-height: none !important;
}

/* SEARCH FORMATING */

#yt-masthead-content {
  padding-top: 5px !important;
}

#watch7-content{   
  border-radius: 9px !important; 
  color: #cecece !important;
  
}

#masthead-search-term{   
  color: #cecece !important;
  
}

/* TITLE AND DESCRIPTION FORMATING */

#watch7-headline {
    right: 10px !important;
    text-align: right !important;
    width: 600px !important;
    float: right !important;
}

#eow-description {
    text-align: justify !important;
}

#watch-description {
    margin-top: -30px !important;
}

.watch-sidebar, #watch7-main {
    min-width: 0 !important;
    max-width: none !important;
}

#content {
    margin-top: 25px !important;
}

#page-container, #page-container, #watch7-sidebar, #page {
    padding: 0 !important;
    margin-left: auto !important;
    margin-right: auto !important;
    margin-bottom: -200px !important;
    margin-top: 15px !important;
}


/* PAGE AND PLAYER FORMATING */

#player, #watch7-content div, #watch7-sidebar {
    background: transparent !important;
}

.watch-small {
    max-width: 600px!important;
}

#watch7-main > div {
    float: none !important;
    width: 100% !important;
    position: static !important;
    margin: 0 !important;
}

li.video-list-item {
    width: 31% !important;
    display: inline-block !important;
    margin: 15px 1% !important;
    vertical-align: top !important;
}

#watch-more-related-button {
    width: 100% !important;
    margin-top: 10px !important;
}

#watch7-content {
    border: 1px solid #E6E6E6 !important;
}

#watch7-content div {
    border-left: 0 !important;
    border-right: 0 !important;
}

div.action-panel-content {
    width: 100% !important;
}

#watch-description-extra-info {
    margin-left: auto !important;
}

#watch7-main-container {
    width: 85% !important;
    min-width: 640px !important;
    margin: 0 auto !important;
    padding: 0 !important;
    -webkit-transition: none !important;
    -moz-transition: none !important;
    transition: none !important;
}

#watch7-main-container > div {
    padding-left: 0 !important;
}

#watch7-main, #watch7-video, .watch7-playlist {
    width: 100% !important;
}

#page-container #page {
    width: auto !important;
    margin: 0 !important;
}

#player {
    margin: 0 auto !important;
    position: relative;
    width: 640px;
}

#page-container #page, #player {
    padding-left: 0 !important;
    min-width: 0 !important;
    -webkit-transition: none !important;
    -moz-transition: none !important;
    transition: none !important;
}


/*  HIDE ELEMENTS  */


#watch-discussion, #yt-uix-videoactionmenu-menu, #yt-masthead-signin,  #guide, #watch7-branded-banner, .watch-sidebar-head, #watch-longform-ad, #watch-channel-brand-div {
    display: none !important;
}

.yt-uix-button {
    border-width: 0px !important;
}

#watch8-action-buttons, #watch-action-panels,#footer-container, #footer, .appbar-guide-toggle, #yt-masthead-user {
    display: none !important;
}

button[data-gen204="feature=watch-show-more-metadata"] {
    display: none !important;
}

div#watch-description-extras {
    display: none !important;
}

div.watch-sidebar-section:not(:last-child) {
    display: none;
}


/*  COLORS  */

html body {
    background-color: #181818 !important;
}

#masthead-positioner, #masthead-search-terms, #yt-masthead-container, #masthead-positioner-height-offset, #watch7-sidebar-contents {
    background-color: #181818 !important;
}

a {
    color: #aaa !important;
}

a:hover {
    color: #fff important;
}

span[class="title"] {
    color: #aaa !important;
}

.watch-title, .yt-user-info, .watch-time-text {
    color: #aaa !important;
}

#eow-description, #watch-description-clip, #watch-description-text {
    color: #888 !important;
}

.view-count, .attribution {
    color: #474848 !important;
}
}
